INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020019', 'PROCESSO ANALÍTICO-SINTÉTICO QUE PERMITE CARACTERIZAR, MEDIR E EXPLICAR O PERFIL DE SAÚDE-DOENÇA DA POPULAÇÃO TRABALHADORA COM A PRIORIZAÇÃO DOS PROBLEMAS DE SAÚDE E A IDENTIFICAÇÃO DOS SEUS DETERMINANTES, PARA A PROPOSIÇÃO DE INTERVENÇÕES E AVALIAÇÃO DOS SEUS IMPACTOS; A ANÁLISE DE SITUAÇÃO DE SAÚDE DO TRABALHADOR E DA TRABALHADORA (ASSTT) PODE SER FEITA POR MEIO DA COLETA DE DADOS PRIMÁRIOS E/OU SECUNDÁRIOS - ESTES COM DADOS DOS SISTEMAS DE INFORMAÇÃO, SEJA ELE DA ÁREA DA SAÚDE OU NÃO. A ASSTT DEVE SER CONSTRUÍDA DE FORMA ARTICULADA COM A ATENÇÃO PRIMÁRIA ESPECIALIZADA, E COM TODAS AS ÁREAS DA VIGILÂNCIA EM SAÚDE, COM PARTICIPAÇÃO DE REPRESENTANTES DOS TRABALHADORES, DE MODO A PERMITIR O MONITORAMENTO CONTÍNUO E SISTEMÁTICO DA SITUAÇÃO DE SAÚDE DE UMA DADA POPULAÇÃO EM SEU TERRITÓRIO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020027', 'A EDUCAÇÃO EM SAÚDE DO TRABALHADOR É UM PROCESSO DE CONSTRUÇÃO DE CONHECIMENTOS EM SAÚDE, QUE DEVE SER DESENVOLVIDO ENTRE OS PROFISSIONAIS, GESTORESE OS (AS) TRABALHADORES (AS) EM GERAL. ESSA PRÁTICA POSSIBILITA A TROCA DE EXPERIÊNCIAS, SABERES EM SAÚDE RECONHECENDO OS SABERES PRÉVIOS POR MEIO DO DIÁLOGO E DA PROBLEMATIZAÇÃO, PARA A CONSTRUÇÃO DE CONHECIMENTO EM SAÚDE DE FORMA COMPARTILHADA E QUE POSSIBILITE A PARTICIPAÇÃO DO SUJEITO NO SEU PROCESSO DE CUIDADO, VISANDO A AMPLIAÇÃO DE SUA AUTONOMIA E FORTALECENDO A PARTICIPAÇÃO SOCIAL. O PROCEDIMENTO A SER INFORMADO É A AÇÃO REALIZADA E NÃO O NÚMERO DE PARTICIPANTES DA AÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020035', 'INSPEÇÃO SANITÁRIA EM SAÚDE DO TRABALHADOR É UM CONJUNTO DE PROCEDIMENTOS TÉCNICOS E ADMINISTRATIVOS QUE VISA A PROTEÇÃO DA SAÚDE INDIVIDUAL E COLETIVA DOS TRABALHADORES, POR MEIO DA VERIFICAÇÃO IN LOCO DO CUMPRIMENTO DOS MARCOS LEGAL E REGULATÓRIO SANITÁRIOS RELACIONADOS À SAÚDE E SEGURANÇA NO TRABALHO. A INSPEÇÃO PERMITE A ADOÇÃO DE MEDIDAS DE ORIENTAÇÃO E CORREÇÃO DE SITUAÇÕES QUE POSSAM CAUSAR DANOS À SAÚDE DA POPULAÇÃO, OS INSTRUMENTOS ADMINISTRATIVOS DE REGISTRO DESSA AÇÃO SÃO OS TERMOS DE VISITA, NOTIFICAÇÃO, INTIMAÇÃO, AUTO DE INFRAÇÃO. O PROCEDIMENTO A SER INFORMADO É A AÇÃO REALIZADA E NÃO O NÚMERO DE PARTICIPANTES DA AÇÃO. SOLICITA-SE QUE ESTA AÇÃO SEJA REALIZADA SEGUNDO A INSTRUÇÃO NORMATIVA Nº 3.120/1998.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020043', 'AS INSPEÇÕES SANITÁRIAS EM SAÚDE DO TRABALHADOR CLASSIFICADAS COMO AVALIAÇÃO DE CUMPRIMENTO DE RECOMENDAÇÕES REFEREM-SE A OBSERVAR O ATENDIMENTO, POR PARTE DO ESTABELECIMENTO, DAS MEDIDAS DE ELIMINAÇÃO/CONTROLE DAS EXPOSIÇÕES ÀS SITUAÇÕES E FATORES DE RISCOS E DE OUTRAS MEDIDAS DE PROTEÇÃO À SAÚDE DOS TRABALHADORES RECOMENDADAS NAS AÇÕES PARA MAPEAMENTO DE RISCOS OCUPACIONAIS, INVESTIGAÇÃO DE ACIDENTE DE TRABALHO, DE RELAÇÃO DE DOENÇAS E AGRAVOS COM O TRABALHO E DE SURTOS. A CONDIÇÃO/GRAU DE CUMPRIMENTO DE CADA RECOMENDAÇÃO PODE ESTAR DESCRITA COMO, POR EXEMPLO, CUMPRIDA, NÃO CUMPRIDA, CUMPRIDA COM RESSALVAS OU PARCIALMENTE CUMPRIDA, SEGUIDA DA JUSTIFICATIVA. SOLICITA-SE QUE ESTA AÇÃO SEJA REALIZADA SEGUNDO A INSTRUÇÃO NORMATIVA Nº 3.120/1998.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020051', 'CONSISTE NA BUSCA DE CASOS SUSPEITOS DE ACIDENTES, DOENÇAS E/OU AGRAVOS RELACIONADOS AO TRABALHO (DART) POR MEIO DA PROCURA DE INDIVÍDUOS COM O FIM DE UMA IDENTIFICAÇÃO SINTOMÁTICA, PRINCIPALMENTE DAS DOENÇAS E AGRAVOS DE NOTIFICAÇÃO COMPULSÓRIA NO SINAN. A AÇÃO PODE SER REALIZADA A PARTIR DA IDENTIFICAÇÃO DE RUMORES DE DART NAS REDES SOCIAIS E MÍDIA; IDENTIFICAÇÃO DE CASOS DE DART EM SERVIÇOS DE SAÚDE, EMPRESAS, SINDICATOS, COMUNIDADES, RESIDÊNCIAS, ENTRE OUTROS LOCAIS, POR MEIO DE QUESTIONAMENTOS À EQUIPE MÉDICA, EMPREGADORES E/OU TRABALHADORES. DEVEM SER REGISTRADAS TODAS AS AÇÕES DE BUSCA ATIVA E OU CASOS SUSPEITOS IDENTIFICADOS, INDEPENDENTEMENTE DA CONCLUSÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020060', 'APOIO INSTITUCIONAL É UMA FUNÇÃO GERENCIAL QUE REFORMULA O MODO TRADICIONAL DE SE FAZER COORDENAÇÃO, PLANEJAMENTO, SUPERVISÃO E AVALIAÇÃO EM SAÚDE. DISPARA OFERTAS, ESTRATÉGIAS DE RELAÇÕES INTRA E INTERINSTITUCIONAIS COM DIRETRIZES ORÇAMENTÁRIAS, POLÍTICAS E ORGANIZACIONAIS, VISANDO FACILITAR AS PRÁTICAS DE PLANEJAMENTO E EXECUÇÃO DAS AÇÕES DE SAÚDE DO TRABALHADOR NO TERRITÓRIO. É DIRECIONADO PARA PROPORCIONAR O DIÁLOGO E INTERAÇÃO ENTRE GESTORES E EQUIPES DE VIGILÂNCIA EM SAÚDE DO TRABALHADOR NOS TERRITÓRIOS. ATUA NOS ESPAÇOS DE GESTÃO (COLEGIADOS, SECRETARIAS, CONSELHOS ETC), FORTALECENDO ATORES E INSTÂNCIAS QUE ATUAM NA SAÚDE DO TRABALHADOR E DA TRABALHADORA DA RENAST.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020078', 'REALIZAÇÃO DE ATIVIDADES COM GRUPOS NA TEMÁTICA DE SAÚDE DO TRABALHADOR, SENDO POSSÍVEL IDENTIFICAR DIVERSAS MODALIDADES DE GRUPOS, TAIS COMO: GRUPOS ABERTOS DE ACOLHIMENTO, GRUPOS TEMÁTICOS RELACIONADOS A DETERMINADAS PATOLOGIAS (HIPERTENSÃO, OBESIDADE, DIABETES, GESTANTES TRABALHADORAS, LER/DORT, TRANSTORNOS MENTAIS, INTOXICAÇÕES ETC.) OU DE TRABALHADORES(AS) EXPOSTOS(AS) (AMIANTO, BENZENO, AGROTÓXICOS ETC.), OFICINAS TEMÁTICAS (GERAÇÃO DE RENDA, ARTESANATO), GRUPOS DE MEDICAÇÃO, GRUPOS TERAPÊUTICOS, GRUPOS DE ATIVIDADE FÍSICA, GRUPOS DE QUALIDADE DE VIDA ETC. OBS: AS ATIVIDADES COM GRUPOS DE CARÁTER DE EDUCAÇÃO EM SAÚDE', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020086', 'AS INSPEÇÕES SANITÁRIAS EM SAÚDE DO TRABALHADOR, CLASSIFICADAS COMO MAPEAMENTO DE RISCOS OCUPACIONAIS REFEREM-SE A IDENTIFICAR OS FATORES OU SITUAÇÕES DE RISCOS OCUPACIONAIS / TER O DIAGNÓSTICO DA SITUAÇÃO DOS AMBIENTES DE TRABALHO ATRAVÉS: 1) DE CONHECER O PROCESSO PRODUTIVO, A ORGANIZAÇÃO DO TRABALHO E AS RELAÇÕES DE PRODUÇÃO NO ESTABELECIMENTO, IDENTIFICANDO O TRABALHO REAL PARA ALÉM DO PRESCRITO, A EXEMPLO DA OPERAÇÃO DE MAQUINÁRIO, EQUIPAMENTOS, UTILIZAÇÃO DE SUBSTÂNCIAS OU PRODUTOS (MATÉRIAS-PRIMAS, PRODUTOS FINAIS E INTERMEDIÁRIOS) EM CADA SETOR; 2) IDENTIFICAR OS FATORES E SITUAÇÕES DE RISCO NOS AMBIENTES E PROCESSOS DE TRABALHO POR SETOR OU FASE DO PROCESSO PRODUTIVO DO ESTABELECIMENTO; 3) AVALIAR AS MEDIDAS COLETIVAS E INDIVIDUAIS ADOTADAS PARA PROTEÇÃO DA SAÚDE DO TRABALHADOR; 4) ANALISAR, DE FORMA PRELIMINAR, OS FATORES E SITUAÇÕES DE RISCO EXISTENTES PARA DEFINIÇÃO DE PRIORIDADES DE INTERVENÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020094', 'AS INSPEÇÕES SANITÁRIAS EM SAÚDE DO TRABALHADOR CLASSIFICADAS COMO INVESTIGAÇÃO DE ACIDENTE DE TRABALHO REFEREM-SE A IDENTIFICAR OS FATORES/REDE DE CAUSALIDADE QUE FORAM DETERMINANTES PARA A OCORRÊNCIA DO ACIDENTE DE TRABALHO ATRAVÉS: 1) OBSERVAÇÃO DIRETA DO SISTEMA QUE ORIGINOU O ACIDENTE, COM COLETA DE INFORMAÇÕES E ANÁLISES DE DOCUMENTOS; 2) AVALIAÇÃO DAS MEDIDAS DE PROTEÇÃO COLETIVAS E INDIVIDUAIS EXISTENTES NO SISTEMA E COMPREENSÃO DE QUAIS FALHARAM; 3)COMPREENSÃO DO ACIDENTE ATRAVÉS DA ANÁLISE E SISTEMATIZAÇÃO DAS INFORMAÇÕES COLETADAS, ENTENDENDO QUE NORMALMENTE OS ACIDENTES DECORREM DE FALHAS GERENCIAIS DA POLÍTICA DE SAÚDE E SEGURANÇA DO SISTEMA E DE OUTRAS CONDIÇÕES ORGANIZACIONAIS; 4) EMISSÃO DE PARECER OU DOCUMENTO TÉCNICO CONCLUSIVO DESCREVENDO AS CAUSAS DO ACIDENTE E RECOMENDAÇÕES DE INTERVENÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020108', 'AS INSPEÇÕES SANITÁRIAS EM SAÚDE DO TRABALHADOR CLASSIFICADAS COMO DE ESTABELECIMENTO DA RELAÇÃO ENTRE A DOENÇA E OU AGRAVO COM O TRABALHO REFEREM-SE A IDENTIFICAR NOS AMBIENTES E PROCESSOS DE TRABALHO OS FATORES OU SITUAÇÕES DE RISCOS OCUPACIONAIS QUE TENHAM RELAÇÃO COM AGRAVO OU A DOENÇA APRESENTADA PELO TRABALHADOR OU GRUPO DE TRABALHADORES EM INVESTIGAÇÃO, VISANDO ESTABELECER NEXO DE CAUSALIDADE. A INSPEÇÃO SANITÁRIA PARA ASSOCIAÇÃO ENTRE DIAGNÓSTICO CLÍNICO E TRABALHO DEVE SUBSIDIAR A INVESTIGAÇÃO CLÍNICO-EPIDEMIOLÓGICA DA RELAÇÃO DOENÇA E/OU AGRAVO COM O TRABALHO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020116', 'AS INSPEÇÕES SANITÁRIAS EM SAÚDE DO TRABALHADOR CLASSIFICADAS COMO INVESTIGAÇÃO DE SURTOS/EVENTOS INUSITADOS EM AMBIENTES DE TRABALHO REFEREM-SE A VERIFICAR AS EVIDÊNCIAS/EXISTÊNCIA DAS FONTES DE CONTAMINAÇÃO QUÍMICAS, FÍSICAS OU BIOLÓGICAS, OS AGENTES ETIOLÓGICOS OU OS FATORES DE RISCO RELACIONADOS COM SURTOS OU EVENTOS INUSITADOS, COM O OBJETIVO DE CONTROLAR, AFASTAR, REDUZIR E/OU ELIMINAR O RISCO DE AGRAVOS À SAÚDE, PREVENINDO O SURGIMENTO DE NOVOS CASOS NOS AMBIENTES DE TRABALHO. SEMPRE QUE POSSÍVEL, ESTA AÇÃO DEVE ESTAR COORDENADA COM OS OUTROS SERVIÇOS DA VIGILÂNCIA EM SAÚDE, INCLUSIVE PELA NECESSIDADE, MUITAS VEZES, DA COLETA DE AMOSTRAS (DE ALIMENTOS, PRODUTOS, AGENTE ETIOLÓGICO ETC.) PARA ANÁLISE LABORATORIAL. A ANÁLISE LABORATORIAL PODE AUXILIAR, MAS NÃO DEVE SER PREPONDERANTE AOS ACHADOS CLÍNICO-EPIDEMIOLÓGICO DO CASO SUSPEITO EM INVESTIGAÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020124', 'REALIZAÇÃO DE AÇÕES DE SAÚDE DO TRABALHADOR ARTICULADAS COM O CONTROLE SOCIAL OU REPRESENTAÇÃO DE TRABALHADORES GARANTINDO A PARTICIPAÇÃO DOS TRABALHADORES NESTAS AÇÕES E NO PROCESSO DE FORMULAÇÃO E CONTROLE DAS POLÍTICAS E TEMAS RELACIONADOS À SAÚDE DO TRABALHADOR. PODEM SER REUNIÕES, ENCONTROS, AUDIÊNCIAS, PARTICIPAÇÃO NA ELABORAÇÃO DO PLANO DE TRABALHO DO CEREST E EM AÇÕES DE VIGILÂNCIA, ENTRE OUTRAS AÇÕES DE ARTICULAÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020132', 'CONSISTE EM ESTRATÉGIAS QUE PROMOVAM A ARTICULAÇÃO E A INTEGRAÇÃO DE AÇÕES, SABERES E ESFORÇOS DE DIFERENTES SETORES E ATORES DE INTERESSE À SAÚDE DO TRABALHADOR, NA FORMA DE AÇÕES INTRA E INTERSETORIAIS, QUE ENVOLVAM A REDE DE EDUCAÇÃO, JUSTIÇA, ASSISTÊNCIA SOCIAL, DIREITOS HUMANOS E OUTROS. O INTUITO DESSAS AÇÕES É PLANEJAR, DIVULGAR, PROMOVER E CONSTRUIR OBJETOS COMUNS DE INTERVENÇÃO, NO QUE DIZ RESPEITO À PROMOÇÃO, PREVENÇÃO E PROTEÇÃO DA SAÚDE DOS TRABALHADORES.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0102020140', 'AÇÕES DE VIGILÂNCIA EM SAÚDE DO TRABALHADOR DURANTE A OCORRÊNCIA DE EVENTOS DE EMERGÊNCIA EM SAÚDE PÚBLICA ORIGINADAS OU NÃO A PARTIR DE ATIVIDADES PRODUTIVAS, QUE AFETEM TRABALHADORES DIRETA OU INDIRETAMENTE, INCLUSIVE AQUELES ENVOLVIDOS NA RESPOSTA. AS AÇÕES CONTEMPLAM TRÊS ETAPAS: A REDUÇÃO DE RISCO, MANEJO DA EMERGÊNCIA E A RECUPERAÇÃO. SEMPRE QUE POSSÍVEL, ESTAS AÇÕES DEVEM ESTAR COORDENADAS COM OS OUTROS SERVIÇOS DA VIGILÂNCIA EM SAÚDE E ATORES EXTERNOS, COMO: DEFESA CIVIL, BOMBEIROS, MINISTÉRIO PÚBLICO DO TRABALHO, ENTRE OUTROS. OS INSTRUMENTOS ADMINISTRATIVOS DE REGISTRO DESSA VIGILÂNCIA SÃO OS TERMOS DE INSPEÇÃO, NOTIFICAÇÃO DE DOENÇAS E AGRAVOS, INTIMAÇÃO E AUTO DE INFRAÇÃO, RELATÓRIO DE INSPEÇÃO, DE CONTINGÊNCIA EM SAÚDE DO TRABALHADOR, PLANOS DE AÇÃO, MATRIZ DE RESPONSABILIDADES, ATAS DE REUNIÕES DE COMITÊS OPERATIVOS DE EMERGÊNCIAS EM SAÚDE E DE COMITÊS DE MONITORAMENTO DE EVENTOS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010011', 'CONSISTE NUM PROCEDIMENTO OBSTÉTRICO INVASIVO ONDE É REALIZADA PUNÇÃO DA CAVIDADE AMNIÓTICA PARA A RETIRADA DE DETERMINADO VOLUME DE LÍQUIDO AMNIÓTICO CONTENDO CÉLULAS E PRODUTOS DE ORIGEM FETAL. A PARTIR DA ANÁLISE DO LÍQUIDO AMNIÓTICO, E DO SEU CONTEÚDO. PODE-SE REALIZAR INÚMEROS ESTUDOS VISANDO O DIAGNÓSTICO DE UMA SÉRIE DE DOENÇAS FETAIS. PERMITE REALIZAR DOSAGENS DE CERTAS PROTEÍNAS, PRATICAR ANÁLISES BIOQUÍMICAS, ESTUDO DO DNA, E PRINCIPALMENTE O CARIÓTIPO FETA L(ESTUDO CITOGENÉTICO). A DETERMINAÇÃO DO CARIÓTIPO FETAL É A PRINCIPAL INDICAÇÃO DE AMNIOCENTESE NA ATUALIDADE, UTILIZADA NAS MESMAS SITUAÇÕES DE BIOPSIA DE VILO CORIAL. É REALIZADA ATRAVÉS DE PUNÇÃO ABDOMINAL GUIADA PELO ULTRASSOM, ENDEREÇADA A CAVIDADE AMNIÓTICA PARA A COLETA DO MATERIAL. POR SER ORIENTADA PELO ULTRASSOM, NÃO TEREMOS RISCO DE TRAUMATISMO FETAL DIRETO. É INDOLOR, SEM NECESSIDADE DE PREPARO ESPECIAL PRÉVIO E COM BAIXOS RISCOS ASSOCIADOS. É PREFERÍVEL A REALIZAÇÃO A PARTIR DE 15 SEMANAS DE GESTAÇÃO, VISTO QUE NESTA FASE TORNA-SE PROCEDIMENTO DE MENOR RISCO E DE EXECUÇÃO MAIS FÁCIL. OCASIONALMENTE, A AMNIOCENTESE PODE SER REALIZADA PARA DIMINUIR O VOLUME DO LÍQUIDO AMNIÓTICO.', '202507', 1);
