INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010208', 'ESTE PROCEDIMENTO DE BIÓPSIA É A ÚNICA MANEIRA DE FAZER O DIAGNÓSTICO DEFINITIVO DO CÂNCER DE FÍGADO. CONSISTE NA REMOÇÃO DE UMA PEQUENA QUANTIDADE DE TECIDO PARA EXAME AO MICROSCÓPIO. A BIÓPSIA EM CUNHA OU POR FRAGMENTO PARA DIAGNÓSTICO DO CÂNCER DE FÍGADO, EM ALGUNS CASOS PODE NÃO SER OBTIDA ANTES DA PRÓPRIA CIRURGIA. NESTES CASOS É REALIZADA A BIÓPSIA DURANTE A CIRURGIA QUE PODE SER AINDA INCISIONAL (COM REMOÇÃO DE UM PEDAÇO DE TUMOR) OU EXCISIONAL (REMOÇÃO DE TODO O TUMOR E ALGUM TECIDO DE FÍGADO NORMAL CIRCUNDANTE) PODE SER REALIZADA DURANTE A CIRURGIA. ALÉM DISSO, PODE AINDA SER COLHIDAS DURANTE UMA LAPAROSCOPIA. ISSO PERMITE QUE AO MÉDICO VISUALIZAR A SUPERFÍCIE DO FÍGADO E COLHER TAMBÉM AMOSTRAS DE TECIDO DE APARÊNCIA ANORMAIS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010216', 'A BIÓPSIA É A ÚNICA MANEIRA DE FAZER O DIAGNÓSTICO DEFINITIVO DO CÂNCER DE FÍGADO. CONSISTE NA REMOÇÃO DE UMA PEQUENA QUANTIDADE DE TECIDO PARA EXAME AO MICROSCÓPIO. A BIÓPSIA POR PUNÇÃO PARA DIAGNÓSTICO DO CÂNCER DE FÍGADO PODE SER: BIÓPSIA COM AGULHA  CONSISTE NO PROCEDIMENTO ONDE É INSERIDA UMA AGULHA ATRAVÉS DO ABDOME ATÉ O FÍGADO, PODENDO SER UTILIZADOS DIFERENTES TIPOS DE AGULHA: PUNÇÃO ASPIRATIVA POR AGULHA FINA (PAAF) - CONSISTE NA REMOÇÃO DE UMA AMOSTRA DE CÉLULAS TUMORAIS COM UMA AGULHA E SERINGA. BIÓPSIA DE FRAGMENTO COM AGULHA - CONSISTE NA RETIRADA DE FRAGMENTOS DE TECIDO, COM UMA AGULHA DE CALIBRE UM POUCO MAIS GROSSO QUE DA PAAF, ACOPLADA A UMA PISTOLA ESPECIAL.  AMBOS OS PROCEDIMENTOS SÃO GUIADOS POR ULTRASSOM OU TOMOGRAFIA COMPUTADORIZADA AS QUAIS NÃO ESTÃO INCLUÍDAS NO VALOR DO PROCEDIMENTO DE BIÓPSIA, DEVENDO SER APRESENTADA COMO OUTRO PROCEDIMENTO, EMBORA REALIZADO CONCOMITANTEMENTE.', '202507', 1);
