INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010291', 'CONSISTE NA REMOÇÃO DE PEQUENOS FRAGMENTOS DE TECIDO DO ORGANISMO VIVO NO QUAL É COLHIDA UMA AMOSTRA DE TECIDOS OU CÉLULAS PARA POSTERIOR ESTUDO EM LABORATÓRIO.  DESTINA-SE AO DIAGNÓSTICO POR MEIO DE PROCEDIMENTO INVASIVO REALIZADO EM AMBIENTE SEGURO (OU EM BLOCO CIRÚRGICO, SE NECESSÁRIO), SOB ANESTESIA. A AMOSTRA DE TECIDO PARA EXAME HISTOLÓGICO PODE SER RETIRADA ATRAVÉS DE INCISÃO, RASPAGEM TISSULAR, ASPIRAÇÃO OU PUNÇÃO COM AGULHA, RETIRANDO FRAGMENTOS DE LESÃO SUSPEITA. NESTE CASO, DE LESÕES SUSPEITAS DE NERVOS.BIÓPSIA DE NERVO PERIFÉRICO COSTUMAM CAUSAR ALGUM GRAU DE DÉFICIT NEUROLÓGICO E DEVEM  SER REALIZADAS APÓS CUIDADOSA AVALIAÇÃO DA SUA CONTRIBUIÇÃO PARA O TRATAMENTO DA DOENÇA. AS BIÓPSIAS DE NERVO SÃO ÚTEIS EM LESÕES INFLAMATÓRIAS OU INFECCIOSAS (VASCULITES, HANSENÍASE) E EM ALGUMAS FORMAS DE NEUROPATIAS DESMIELINIZANTES E  AMILOIDOSE. O NERVO MAIS BIOPSIADO É O SURAL, GERALMENTE ENTRE O TENDÃO DE AQUILES E O MALÉOLO EXTERNO. EM CASOS DE NEUROPATIA RESTRITA AOS MEMBROS SUPERIORES, OU QUANDO HÁ CONTRA-INDICAÇÕES PARA BIÓPSIA NOS MEMBROS INFERIORES, UM RAMO SUPERFICIAL DO NERVO RADIAL OU O RAMO DORSAL SENSITIVO DO NERVO ULNAR PODEM SER BIOPSIADOS.', '202507', 1);
