INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010410', 'CONSISTE NA REMOÇÃO DE PEQUENOS FRAGMENTOS DE TECIDO DO ORGANISMO VIVO NO QUAL É COLHIDA, POR MEIO DE UMA AGULHA LONGA APROPRIADA E GUIA DESCARTÁVEL PARA BIÓPSIA, UMA AMOSTRA DA GLÂNDULA PARA POSTERIOR ESTUDO EM LABORATÓRIO. NELA RETIRA-SE NO MÍNIMO OITO PUNÇÕES COM COLETA DE FRAGMENTOS TISSULARES DISTINTOS PARA EXAME HISTOPATOLÓGICO, REPRESENTATIVOS DAS DIFERENTES REGIÕES DA GLÂNDULA||COM ÊNFASE NAS ÁREAS SUSPEITAS AO EXAME RETAL OU ULTRASSONOGRAFIA. O MATERIAL DEVE SER COLHIDO POR SEXTANTES. A BIÓPSIA DE PRÓSTATA É REALIZADA POR VIA TRANSRETAL OU TRANSPERINEAL, EM UM AMBIENTE AMBULATORIAL E/OU HOSPITALAR SOB ANESTESIA LOCAL OU SEDAÇÃO E GUIADA POR EXAME DE IMAGEM DE ULTRASSONOGRAFIA TRANSRETAL, SENDO NESTE CASO ASSOCIADA AO PROCEDIMENTO 02.05.02.011-9 -ULTRASSONOGRAFIA DE PROSTATA (VIA TRANSRETAL).', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010429', 'A BIÓPSIA DE PULMÃO CONSISTE NUM PROCEDIMENTO QUE OBJETIVA OBTER PEQUENOS FRAGMENTOS DE TECIDO DO PULMÃO QUE PRECISA DE ESCLARECIMENTO DIAGNÓSTICO, MAIS FREQUENTEMENTE INDICADO PARA ESCLARECER A NATUREZA BENIGNA OU MALIGNA DE ALGUM NÓDULO PULMONAR, MAS PODE SER INDICADA TAMBÉM EM ALGUNS PROCESSOS INFECCIOSOS OU INFLAMATÓRIOS, COM O INTUITO DE REVELAR QUAL O AGENTE INFECCIOSO ESPECÍFICO E ASSIM ORIENTAR O TRATAMENTO CORRETO. ESTE PROCEDIMENTO É REALIZADO COM UMA AGULHA ESPECIAL, UTILIZANDO-SE A TOMOGRAFIA PARA LOCALIZAR A LESÃO A SER BIOPSIADA E ORIENTAR O TRAJETO MAIS SEGURO DA AGULHA. SALVO ALGUMAS SITUAÇÕES EXCEPCIONAIS, NÃO HÁ NECESSIDADE DE INTERNAR, SENDO REALIZADO EM REGIME AMBULATORIAL. DE UMA FORMA GERAL, O PROCEDIMENTO PODE SER REALIZADO COM ANESTESIA LOCAL, SEDAÇÃO OU MESMO ANESTESIA GERAL E DURA ENTRE 30 MINUTOS E 2 HORAS, A DEPENDER DA COMPLEXIDADE DE CADA SITUAÇÃO. APÓS O PROCEDIMENTO, HAVERÁ UM PERÍODO DE OBSERVAÇÃO CLÍNICA DE CERCA DE 4 HORAS EM AMBIENTE HOSPITALAR. MUITAS VEZES A BIÓPSIA PODE SER REALIZADA COM AUXÍLIO DE AGULHAS, QUE COLETA PEQUENA AMOSTRA DO TECIDO PULMONAR, PARA EXAME ANATOMOPATOLÓGICO. PODE SER AGULHA FINA (PAAF) OU POR AGULHA GROSSA (CORE BIOPSY), POIS UMA AGULHA DE CALIBRE MAIOR REMOVE UM OU MAIS NÚCLEOS DE TECIDO. A PUNÇÃO É UM PROCEDIMENTO RÁPIDO, REALIZADA COM ANESTÉSICO LOCAL.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010437', 'CONSISTE NO PROCEDIMENTO MEDIANTE O QUAL SE COLHE UM FRAGMENTO DE TECIDO ORGÂNICO OU UMA PEQUENA QUANTIDADE DE CÉLULAS (AMOSTRA) PARA POSTERIOR DETERMINAÇÃO DA SUA NATUREZA, EM LABORATÓRIO. A BIÓPSIA RENAL É UTILIZADA SEMPRE QUE OUTROS EXAMES NÃO SÃO CAPAZES DE ESCLARECER COM PRECISÃO A DOENÇA RENAL. NELA RETIRA-SE UM FRAGMENTO DO RIM, DE UM A DOIS CENTÍMETROS POR MEIO DE UMA AGULHA LONGA APROPRIADA. A BIÓPSIA RENAL É FEITA EM UM AMBIENTE HOSPITALAR SOB ANESTESIA LOCAL OU SEDAÇÃO E GUIADA POR EXAME DE IMAGEM COMO ULTRASSONOGRAFIA E O PROCESSO É REPETIDO MAIS DE UMA VEZ PARA QUE SE OBTENHA PELO MENOS DUAS AMOSTRAS DE LOCAIS DIFERENTES DO RIM. TODO O PROCEDIMENTO DURA CERCA DE 20 A 30 MINUTOS E APÓS O PROCEDIMENTO, O PACIENTE DEVE MANTER-SE EM OBSERVAÇÃO POR ALGUMAS HORAS, EM REPOUSO, DEVIDO AO RISCO DE SANGRAMENTO.', '202507', 1);
