INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010534', 'CONSISTE EM UM PROCEDIMENTO DIGITAL DE ALTA PRECISÃO, ALTA COMPLEXIDADE E MINIMAMENTE INVASIVO, REALIZADO POR MEIO DE UMA AGULHA DE BIÓPSIA.É O MÉTODO MAIS UTILIZADO PARA INVESTIGAR LESÕES EM ÁREAS PROFUNDAS DO CÉREBRO DE DIFÍCIL ACESSO CIRÚRGICO, SÍTIOS CONHECIDOS COMO ÁREAS ELOQUENTES PELO FATO DE QUE LESÕES NESSAS REGIÕES PODEM OCASIONAR SEQUELAS GRAVES E IRREVERSÍVEIS. POSSIBILITA A OBTENÇÃO DE AMOSTRA TECIDUAL DO ALVO ALMEJADO COM PRECISÃO MILIMÉTRICA E MENOR RISCO PARA O INDIVÍDUO, VISTO QUE É POSSÍVEL DEFINIR EXATAMENTE ONDE SE ENCONTRA A ALTERAÇÃO POR MEIO DAS RADIOGRAFIAS OBTIDAS COM DIFERENTES ANGULAÇÕES. O PROCEDIMENTO É REALIZADO UTILIZANDO-SE UM SISTEMA DE COORDENADAS TRIDIMENSIONAIS, COM VISTAS A LOCALIZAR PEQUENOS ALVOS. OS EXAMES DE IMAGEM NÃO ESTÃO INCLUÍDOS NO VALOR DESTE PROCEDIMENTO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010542', 'CONSISTE NA UTILIZAÇÃO DO ARSENAL TECNOLÓGICO DISPONÍVEL  PARA ESTUDO/EXAMES DE IMAGEM CAPAZ DE DIAGNOSTICAR COM  PRECISÃO A MAIORIA DAS PATOLOGIAS, TRADUZINDO, COM RARAS  EXCEÇÕES, ACHADOS PROPEDÊUTICOS, EM BENEFÍCIO DO  PACIENTE, SEMPRE ASSOCIADO A SINAIS CLÍNICOS E  PATOLÓGICOS. EXAME DE IMAGEM ASSOCIADO À BIÓPSIA  PERCUTÂNEA É INDICADO QUANDO HÁ NECESSIDADE DE OBTER  INFORMAÇÕES PORMENORIZADAS A RESPEITO DA ANATOMIA DE UM OU  DE VÁRIOS ÓRGÃOS DO CORPO, PRODUZINDO, INCLUSIVE, IMAGENS  QUE PERMITEM RECONSTRUÇÕES MULTIPLANARES E TRIDIMENSIONAIS  DE ALTÍSSIMA DEFINIÇÃO, REALIZANDO VARREDURAS AMPLAS E  DETALHADAS DO CORPO EM CURTO ESPAÇO DE TEMPO. A BIÓPSIA  PERCUTÂNEA É MENOS INVASIVA QUE A REALIZADA POR  PROCEDIMENTO CIRÚRGICO E PERMITE MAIOR PRECISÃO NO  RESULTADO. NO VALOR DESTE PROCEDIMENTO NÃO ESTÃO  INCLUÍDOS OS VALORES DOS EXAMES DE IMAGEM.', '202507', 1);
