INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010569', 'QUALQUER PROCEDIMENTO CIRÚRGICO DA MAMA COM FINALIDADE DIAGNÓSTICA OU TERAPÊUTICA, QUANDO SE TRATAR DE LESÕES NÃO PALPÁVEIS OU PALPÁVEIS DE ATÉ 3 (TRÊS) CM NO SEU MAIOR DIÂMETRO COM DIAGNÓSTICO CLÍNICO, RADIOLÓGICO, ULTRA-SONOGRÁFICO, CITOLÓGICO OU HISTOPATOLÓGICO DE LESÃO BENÍGNA OU MALÍGNA. INCLUI A NODULECTOMIA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010585', 'PROCEDIMENTO INDICADO NÃO SÓ PARA AS DISPLASIAS, MAS PRINCIPALMENTE NA SUSPEITA DE NEOPLASIA MALÍGNA (C50) E PARA DIAGNÓSTICO DE NEOPLASIA BENÍGNA (D24), QUE COMUMENTE SE APRESENTAM COMO LESÃO ÚNICA. O RESULTADO DO EXAME CITOLÓGICO PODE, EM UMA MINORIA DE CASOS, NÃO SER DE MALIGNIDADE.QUANTIDADE MÁXIMA 02 QUANDO PRECEDIDA EM AMBAS AS MAMAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010593', 'CONSISTE REALIZAÇÃO DE PUNÇÃO COM AGULHA NA REGIÃO SUBOCCIPITAL, (PUNÇÃO DE UMA GRANDE CISTERNA DO CÉREBRO), PARA FINS DE DIAGNÓSTICO,(ANÁLISE DO LÍQUIDO CEFALORRAQUIDIANO), PARA A ADMINISTRAÇÃO DE MEDICAMENTOS E PARA O DESEMPENHO DA MIELOGRAFIA.  A CABEÇA DO PACIENTE É FORTEMENTE DOBRADA,  O TUBÉRCULO OCCIPITAL EXTERNO E O PROCESSO ESPINHOSO DA 2ª VÉRTEBRA CERVICAL, SÃO DETERMINADOS. NO MEIO DA DISTÂNCIA ENTRE ELES, UM ANESTÉSICO DA PELE É FEITO. A AGULHA É INSERIDA ESTRITAMENTE NO PLANO SAGITAL ATÉ O FINAL DA SUA REPOSIÇÃO NO OSSO OCCIPITAL. QUANDO O FIM DA AGULHA ATINGE A GRANDE CISTERNA APÓS A EXTRAÇÃO DO MANDRIL, O LÍQUIDO CEFALORRAQUIDIANO COMEÇA A FLUIR DA AGULHA. PODE SER REALIZADA TANTO NA POSIÇÃO RECLINADA COMO NA POSIÇÃO SENTADA DO PACIENTE.  EXIGE MUITA CAUTELA E HABILIDADE DO PROFISSIONAL QUE REALIZA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010607', 'CONSISTE DE PROCEDIMENTO CIRÚRGICO AMBULATORIAL, SOB ANESTESIA||LOCAL, INDICADO PARA O DIAGNOSTICO DE LESÃO MAMARIA IMPALPÁVEL OU||PALPÁVEL COM MAIS DE 02 (DOIS) CM, COM SUSPEITA DE CÂNCER QUE UTILIZA AGULHA GROSSA, ESPECIFICA, DESCARTÁVEL, ACOPLADA A PISTOLA DEDICADA A ESTA FINALIDADE E FORNECE FRAGMENTOS TISSULARES (04 NO MINIMO), PARA EXAME HISTOPATOLÓGICO,CUJO RESULTADO DE EXAME PATOLÓGICO PODE EM UMA MAIORIA DE CASOS NÃO SER DE MALIGNIDADE. QUANTIDADE MÁXIMA 02(DOIS) QUANDO PROCEDIDA EM AMBAS AS MAMAS.||', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010615', 'CONSISTE NO PROCEDIMENTO REALIZADO PARA PUNÇÃO DA CAVIDADE ABDOMINAL ATRAVÉS DA PARTE DE TRÁS DA CÚPULA VAGINAL. PODE SER FEITA A PARTIR DE UM ALVO TERAPÊUTICO PARA A EXTRAÇÃO DE EXSUDADO INFLAMATÓRIO E ADMINISTRAÇÃO DE AGENTES ANTIMICROBIANOS, BEM COMO UM PASSO PRELIMINAR ANTES COLPOTOMIA. É USADO PARA CONFIRMAR O DIAGNÓSTICO DE GRAVIDEZ ECTÓPICA, RUPTURA UTERINA E OUTROS ÓRGÃOS INTERNOS; PARA CONFIRMAR OU EXCLUIR O DIAGNÓSTICO DE PELVE PERITONITE OU PERITONITE GERAL; PARA DETERMINAR A NATUREZA DOS TUMORES SACULARES EXSUDADO DOS OVÁRIOS E TROMPAS DE FALÓPIO (SE NÃO HOUVER SUSPEITA DE MALIGNIDADE). CAUSA GRANDE DESCONFORTO, NÃO DEIXA CICATRIZ. O POSICIONAMENTO DA AGULHA É COMUMENTE GUIADO POR TOMOGRAFIA COMPUTADORIZADA OU EQUIPAMENTO DE RAIOS X. OS EXAMES DE IMAGEM NÃO ESTÃO INCLUÍDOS NO VALOR DO PROCEDIMENTO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010623', 'CONSISTE NA PUNÇÃO POR AGULHA FINA (PAAF) AO NÍVEL DO DUCTO DEFERENTE OU CANAL DEFERENTE QUE CONDUZ OS ESPERMATOZOIDES A PARTIR DO EPIDÍDIMO, LOCAL ONDE ELES SÃO ARMAZENADOS APÓS SEREM PRODUZIDOS NOS TESTÍCULOS. É UMA CONTINUAÇÃO DIRETA DA CAUDA DO EPIDÍDIMO, E TEM ENTRE 30 E 40CM COM A PARTE TESTICULAR, PARTE FUNICULAR, PARTE INGUINAL, PARTE PÉLVICA E AMPOLA DO DUCTO DEFERENTE. É REALIZADA QUANDO HÁ SUSPEITA DE MASSA OU NÓDULOS SUPERFICIAIS QUE CAUSAM SUA OBSTRUÇÃO. É AUXILIADO DE MÉTODOS DE IMAGEM, COMO ULTRASSONOGRAFIA, AUXILIA A IDENTIFICAÇÃO EXATA DO NÓDULO E PERMITE A VISUALIZAÇÃO SIMULTÂNEA DA COLETA DO MATERIAL, GARANTINDO MAIOR EFICÁCIA E MELHORES RESULTADOS MESMO EM NÓDULOS MUITOS PEQUENOS E ÁREAS MAL DELIMITADAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0201010631', 'A PUNÇÃO LOMBAR CONSISTE NA COLETA DO LÍQUIDO CEFALORRAQUIDIANO (LÍQUOR) NA MEDULA ESPINHAL, ATRAVÉS DA UTILIZAÇÃO DE UMA AGULHA, PARA EXAME CITOLÓGICO E TAMBÉM PARA INJEÇÃO DE QUIMIOTERAPIA COM A FINALIDADE DE IMPEDIR O APARECIMENTO (PROFILAXIA) DE CÉLULAS LEUCÊMICAS NO SNC OU PARA DESTRUÍ-LAS QUANDO EXISTIR DOENÇA (MENINGITE LEUCÊMICA) NESSE LOCAL. SUA PRINCIPAL UTILIZAÇÃO É NO DIAGNÓSTICO DE INFECÇÕES MENÍNGEAS, BEM COMO DE OUTRAS CONDIÇÕES NEUROLÓGICAS. A MEDULA ESPINHAL É PARTE DO SISTEMA NERVOSO, QUE TEM A FORMA DE CORDÃO, E POR ISSO É CHAMADA DE CORDÃO ESPINHAL. É FEITA NA MAIORIA DAS VEZES COM ANESTESIA LOCAL E POUCAS VEZES COM ANESTESIA GERAL. NESSE ÚLTIMO CASO, É INDICADO EM PESSOAS QUE NÃO COOPERAM COM O EXAME.', '202507', 1);
