INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010015', 'CONSISTE NA REALIZAÇÃO DO EXAME QUE EXPRESSA A QUANTIDADE DE ÁGUA COM CARGA VARIÁVEL DE SOLUTO, DEPURADO DO PLASMA POR UNIDADE DE TEMPO. A DIFERENÇA ENTRE O VOLUME TOTAL DE URINA E O CLEARANCE OSMOLAR É CHAMADA CLEARANCE DE ÁGUA LIVRE. BASEADO NA PARTE DA FUNÇÃO TUBULAR QUE ENVOLVE REABSORÇÃO DE FLUIDOS E ELETRÓLITOS COM A FORMAÇÃO DE ÁGUA LIVRE, ESTA ATIVIDADE É UMA DAS ÚLTIMAS FUNÇÕES RENAIS A SER PERDIDA. QUANDO A REABSORÇÃO DE ÁGUA NÃO MAIS ACONTECE NORMALMENTE, A EXCREÇÃO DE ÁGUA LIVRE AUMENTA. MEDE A HABILIDADE DOS RINS EM MANTER A TONICIDADE E O BALANÇO HÍDRICO, SENDO ESTAS FUNÇÕES ESPECÍFICAS AVALIADAS POR MEDIDAS DE CONCENTRAÇÕES DE SOLUTOS DA URINA (TESTE DE CONCENTRAÇÃO E DILUIÇÃO). PODE SER UTILIZADO O SANGUE (TUBO SEM ANTICOAGULANTE) OU URINA (FRASCO SEM CONSERVANTE).', '202507', 1);
