INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010058', 'CONSISTE NA COLETA DE SANGUE COM A FINALIDADE DE AVALIAR O PROCESSAMENTO DE ELEVADAS CONCENTRAÇÕES DE GLICOSE.PERMITE DIAGNOSTICAR DIABETES, PRÉ DIABETES, RESISTÊNCIA À INSULINA E OUTRAS ALTERAÇÕES DAS CÉLULAS PANCREÁTICAS, ALÉM DE IDENTIFICAR A DIABETES GESTACIONAL. É NECESSÁRIO JEJUM DE 8 A 14 HORAS. O PACIENTE INGERE, NO LABORATÓRIO, GLICOSE ANISTRA E SÃO REALIZADAS COLETAS DE SANGUE EM 5 MOMENTOS.', '202507', 1);
