INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010082', 'CONSISTE NA DETERMINAÇÃO DA OSMOLARIDADE POR MEIO DE MÉTODOS LABORATORIAIS OU CÁLCULO. POR CÁLCULO É A SOMA DAS CONCENTRAÇÕES DE PARTÍCULAS OSMOTICAMENTE ATIVAS NO SANGUE, COMO SÓDIO, POTÁSSIO, GLICOSE E URÉIA. EM LABORATÓRIO POR MEIO DE MÉTODOS COMO A OSMOMETRIA, QUE MEDE A PRESSÃO OSMÓTICA DE UMA SOLUÇÃO EM COMPARAÇÃO COM UMA SOLUÇÃO DE REFERÊNCIA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010090', 'A 5-NUCLEOTIDASE É UMA FOSFATASE AMPLAMENTE DISTRIBUÍDA NOS TECIDOS E A DOSAGEM DESTA ENZIMA É USADA NO DIAGNOSTICO DE DOENÇAS HEPATOBILIARES. ENCONTRA-SE ELEVADA NA OBSTRUÇÃO BILIAR, NA COLESTASE INTRA-HEPÁTICA E NA CIRROSE BILIAR. AUMENTO DISCRETOS OU NÍVEIS NORMAIS SÃO ENCONTRADOS NAS DOENÇAS PARENQUIMATOSAS HEPÁTICAS. NÍVEIS ELEVADOS PODEM SER OBSERVADOS DURANTE O USO DE ANTICONVULSIVANTES. PODE SER DETERMINADA ATRAVÉS DE ESPECTROFOTOMETRIA CINÉTICA.', '202507', 1);
