INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010341', 'CONSISTE NO EXAME DE SANGUE QUE ANALISA PRINCIPALMENTE ALTERAÇÕES NO CORAÇÃO OU FÍGADO, PARA AVALIAR O PROGRESSO DE DOENÇAS E AUXILIAR NO TRATAMENTO DE DOENÇAS DO MÚSCULO DO CORAÇÃO, INFARTO DO MIOCÁRDIO, DE ANEMIAS MEGALOBLÁSTICAS E HEMOLÍTICAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010350', 'A DESIDROGENASE GLUTÂMICA É UMA ENZIMA MITOCONDRIAL. ESSA ENZIMA É ENCONTRADA, EM QUANTIDADES MÍNIMAS, NO SORO. ENQUANTO QUE EM SITUAÇÃO DE DOENÇA HEPÁTICA É ENCONTRADA COM ATIVIDADE AUMENTADA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010368', 'A DESIDROGENASE LÁTICA É UMA ENZIMA QUE CATALIZA A CONVERSÃO DE LACTATO A PIRUVATO E É LIBERADA EM QUADROS DE INJÚRIA TISSULAR. PORTANTO A ELEVAÇÃO DOS NÍVEIS DE DESIDROGENASE LÁTICA OCORRE, POR EXEMPLO, EM NEOPLASIAS, HIPÓXIA, CARDIOPATIAS, INFLAMAÇÕES, HIPOTIREOIDISMO, HEPATITES, PANCREATITE E OBSTRUÇÃO INTESTINAL.', '202507', 1);
