INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010406', 'A DOSAGEM DE FOLATO É INDICADA PARA A DETERMINAÇÃO DA DEFICIÊNCIA DE ÁCIDO FÓLICO E PARA O MONITORAMENTO DA TERAPIA COM FOLATO. DEVE SER INCLUÍDA NA INVESTIGAÇÃO DA ETIOLOGIA DE ANEMIAS MACROCÍTICA E MEGALOBLÁSTICA, NO ALCOOLISMO E NA SÍNDROME DA ALÇA CEGA INTESTINAL.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010414', 'A FOSFATASE ÁCIDA TOTAL, EM PESSOAS DO SEXO MASCULINO, É REPRESENTADA EM 50% PELA FRAÇÃO PROSTÁTICA. JÁ EM INDIVÍDUOS DO SEXO FEMININO A FOSFATASE ÁCIDA TOTAL É PROVENIENTE DO FÍGADO, DOS ERITRÓCITOS E DAS PLAQUETAS. PORTANTO, ENCONTRA-SE AUMENTADA, POR EXEMPLO, EM PROCESSOS DE EXCESSIVA DESTRUIÇÃO PLAQUETÁRIA, DOENÇAS HEMOLÍTICAS, METÁSTASE DE CÂNCER ÓSSEO E NO CÂNCER DE PRÓSTATA ( NOS CASOS EM QUE HÁ AUMENTO DOS NÍVEIS DA FRAÇÃO PROSTÁTICA).', '202507', 1);
