INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010465', 'A GAMA GT É UM MARCADOR SENSÍVEL DE COLESTASE HEPATOBILIAR E DE USO DO ÁLCOOL. TENDE A SE ELEVAR EM DOENÇAS HEPÁTICAS E PANCREÁTICAS. A LIBRAÇÃO DE GAMA GT NO SORO REFLETE O EFEITO TÓXICO DO ÁLCOOL E OUTRAS DROGAS NA ESTRUTURA MICROSSOMAL NAS CÉLULAS HEPÁTICAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010473', 'A DOSAGEM DE GLICOSE, TAMBÉM CHAMADA DE GLICEMIA,  É UTILIZADA NO DIAGNÓSTICO E NO MONITORAMENTO DO DIABETES MELLITUS E NOS DISTÚRBIOS DA HOMEOSTASE GLICÊMICA. ALÉM DISSO, É ÚTIL NO RASTREAMENTO DO DIABETES GESTACIONAL.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010481', 'A DEFICIÊNCIA DE GLICOSE-6-FOSFATO DESIDROGENASE É UMA ENZIMOPATIA COMUM (GENÉTICA) LIGADA AO CROMOSSOMO X, COM INCIDÊNCIA EM ATÉ 10% DA POPULAÇÃO. PODE LEVAR A CRISES DE HEMÓLISE, INDUZIDA POR DROGAS, POR INFECÇÕES BACTERIANAS E VIRÓTICAS E PELA INGESTÃO DE FAVA. PODE SE MANIFESTAR COMO ANEMIA ESFEROCÍTICA E ICTERÍA NEONATAL.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010490', 'A SÍNTESE DE HAPTOGLOBINA É ESTIMULADA PELA INFLAMAÇÃO. A HAPTOGLOBINA É UMA GLOBINA SINTETIZADA PELO FÍGADO E QUE SE LIGA À HEMOGLOBINA LIVRE. ESSA LIGAÇÃO É RESPONSÁVEL PELA DIMINUIÇÃO DOS NÍVEIS EM EPISÓDIOS AGUDOS DE HEMÓLISE.', '202507', 1);
