INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010511', 'A HIDROXIPROLINA É UM AMINOÁCIDO BASTANTE FREQÜENTE NA COMPOSIÇÃO DO COLÁGENO E SUA EXCREÇÃO URINÁRIA REFLETE EM PARTE A REABSORÇÃO DA MATRIZ ÓSSEA. MAIS RECENTEMENTE A DOSAGEM DA HIDROXIPROLINA NA URINA DE 24 HORAS FOI SUBSTITUÍDA PELA DOSAGEM DESSE AMINOÁCIDO NA URINA COLHIDA DURANTE 2 HORAS APÓS JEJUM DE 10 HORAS. MÉTODO: HPLC (CROMATOGRAFIA LIQUIDA DE ALTA DEFINIÇÃO). SINONÍMIA: OH PROLINA', '202507', 1);
