INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010538', 'O LACTATO É PRODUZIDO PELO ORGANISMO APÓS A GLICÓLISE, PARA O FORNECIMENTO DE ENERGIA EM CONDIÇÕES ANAERÓBICAS. DESSA FORMA, A DETERMINAÇÃO DA CONCENTRAÇÃO SANGUÍNEA DO LACTATO PERMITE AVALIAR, INDIRETAMENTE, A ACIDOSE METABÓLICA APÓS A ATIVIDADE FÍSICA E EM SITUAÇÕES PATOLÓGICAS NAS QUAIS ESTA VIA DE OBTENÇÃO DE ENERGIA FOI UTILIZADA.', '202507', 1);
