INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010562', 'O MAGNÉSIO É UM DOS CÁTIONS MAIS ABUNDANTES NO ORGANISMO HUMANO, ATUANDO COMO COFATOR ESSENCIAL PARA ENZIMAS LIGADAS À RESPIRAÇÃO CELULAR, À GLICÓLISE E AO TRANSPORTE DE OUTROS CÁTIONS. ALÉM DISSO, O MAGNÉSIO É ESSENCIAL PARA A PRESERVAÇÃO DA ESTRUTURA MOLECULAR DO DNA, DO RNA E DOS RIBOSSOMOS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010570', 'AS MUCOPROTEÍNAS SÃO GLICOPROTEÍNAS DE FASE AGUDA. ELEVAM-SE, CONSIDERAVELMENTE, NOS PROCESSOS INFLAMATÓRIOS AGUDOS, CONSTITUINDO UM IMPORTANTE ÍNDICE DA ATIVIDADE REUMÁTICA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010589', 'O PIRUVATO É ORIGINADO PELA QUEBRA DA GLICOSE E PODE SER CONVERTIDO EM LACTATO, ALANINA OU OXALOACETATO OU, ENTÃO, DEGRADADO DANDO ORIGEM AO ACETIL-COA E AO GÁS CARBÔNICO. O PIRUVATO PODE SE ACUMULAR POR EXCESSO DE PRODUÇÃO OU POR METABOLIZAÇÃO DEFICIENTE. A DOSAGEM DE PIRUVATO, EM CONJUNTO COM O LACTATO, É ÚTIL NA AVALIAÇÃO DE DIVERSOS DESARRANJOS METABÓLICOS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010597', 'AS PORFIRINAS SÃO DESORDENS HEREDITÁRIAS OU ADQUIRIDAS DE ENZIMAS ESPECÍFICAS NA BIOSSÍNTESE DO HEME. AS PORFIRINAS SÃO CLASSIFICADAS EM HEPÁTICAS OU EM ERITROPOIÉTICAS, DEPENDENDO DO SÍTIO PRIMÁRIO DE SUPERPRODUÇÃO OU ACUMULAÇÃO DA PORFIRINA OU DO SEU PRECURSOR. OS PRODUTOS DO METABOLISMO INTERMEDIÁRIO DO HEME OU SEU DERIVADOS SÃO EXCRETADOS NA URINA NESSAS PATOLOGIAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010600', 'A DOSAGEM DE POTÁSSIO É ÚTIL NA AVALIAÇÃO DO EQUILÍBRIO HIDROELETROLÍTICO E ACIDOBÁSICO. A MONITORIZAÇÃO DO POTÁSSIO É UTILIZADA NO ACOMPANHAMENTO DE PACIENTES EM TERAPIA COM DIURÉTICOS, EM NEFROPATIAS, NA CETOACIDOSE DIABÉTICA, NO MANEJO DA HIDRATAÇÃO PARENTERAL E NA INSUFICIÊNCIA HEPÁTICA.', '202507', 1);
