INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010627', 'A ALBUMINA É A PROTEÍNA MAIS ABUNDANTE NO PLASMA E SUA FUNÇÃO PRIMÁRIA É MANTER A PRESSÃO COLOIDOSMÓTICA DO PLASMA. EM CONDIÇÕES NORMAIS, ESPERA-SE ENCONTRAR UMA RAZÃO ALBUMINA/GLOBULINA MAIOR OU IGUAL A 1.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010635', 'O SÓDIO É O PRINCIPAL CÁTION EXTRACELULAR, ALÉM DISSO É O DETERMINANTE PRIMORDIAL DA OSMOLARIDADE CELULAR. ALGUNS FATORES REGULAM A HOMEOSTASIA DO BALANÇO DO SÓDIO, TAIS COMO, ALDOSTERONA E HORMÔNIO ANTIDIURÉTICO. A DOSAGEM DE SÓDIO É ÚTIL NA AVALIAÇÃO DOS DISTÚRBIOS HIDROELETROLÍTICOS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010643', 'A TRANSAMINASE GLUTAMICO-OXALACETICA É UMA ENZIMA ENCONTRADA EM ALTAS QUANTIDADES NO MÚSCULO CARDÍACO, ESQUELÉTICO, CÉLULAS HEPÁTICAS E, EM MENOR QUANTIDADE, NO PÂNCREAS E NOS RINS. A DOSAGEM DE TRANSAMINASE GLUTAMICO-OXALACETICA É ÚTIL, POR EXEMPLO, NO DIAGNÓSTICO DO INFARTO DO MIOCÁRDIO, DAS DOENÇAS HEPÁTICAS, DA PANCREATITE AGUDA, DA OPERAÇÃO CARDÍACA, DA CATETERIZAÇÃO CARDÍACA, DA DISTROFIA MUSCULAR, DA MONONUCLEOSE, DA DOENÇA RENAL AGUDAE DE CONVULSÕES RECENTES.', '202507', 1);
