INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010694', 'A UREIA É A PRINCIPAL FONTE DE EXCREÇÃO DO NITROGÊNIO, ORIGINA-SE DO METABOLISMO HEPÁTICO DAS PROTEÍNAS E É EXCRETADA NOS RINS. DESSA FORMA, A UREIA ESTÁ DIRETAMENTE RELACIONADA À FUNÇÃO METABÓLICA HEPÁTICA E À FUNÇÃO RENAL. SUA CONCENTRAÇÃO PODE VARIAR, POR EXEMPLO, COM A DIETA E COM A HIDRATAÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010708', 'A VITAMINA B12 TEM PAPEL IMPORTANTE NA HEMATOPOIESE, NA FUNÇÃO NEURAL, NO METABOLISMO DO ÁCIDO FÓLICO E NA SÍNTESE ADEQUADA DE DNA. A ABSORÇÃO DE VITAMINA B12 É DEPENDENTE DA PRESENÇA DO FATOR INTRÍNSECO (SECRETADO PELA CÉLULAS PARIETAIS DO ESTÔMAGO).', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010716', 'A ELETROFORESE DE LIPOPROTEÍNAS É ÚTIL NA CARACTERIZAÇÃO DAS DISLIPIDEMIAS SECUNDÁRIAS E PRIMÁRIAS. OS LIPÍDEOS CIRCULAM NO PLASMA COMBINADOS A PROTEÍNAS E PODEM SER SEPARADOS POR MEIO DE ELETROFORESE, RECEBENDO NOMES DE ACORDO COM SUA MOBILIDADE: HDL (ALFA-LIPOPROTEÍNA) MIGRAM COM AS ALFA-1-GLOBULINAS; LDL (BETA-LIPOPROTEÍNAS) MIGRAM COM AS BETA-GLOBULINAS; VLDL (PRÉ-BETALIPOPROTEINAS) MIGRAM COM AS ALFA-2- GLOBULINAS; E QUILOMÍCRONS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010724', 'ELETROFORESE É UMA TÉCNICA LABORATORIAL USADA PARA SEPARAR OS GRUPOS DE PROTEÍNAS DO SORO. A ELETROFORESE DE PROTEÍNAS É UTILIZADA NA TRIAGEM DE ANORMALIDADES DAS PROTEÍNAS SÉRICAS. AS PRINCIPAIS FRAÇÕES PROTEICAS, DE ACORDO COM A ELETROPOSITIVIDADE SÃO AS SEGUINTES: ALBUMINA, ALFA 1 GLOBULINA, ALFA 2 GLOBULINA, BETAGLOBULINA E GAMAGLOBULINA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010732', 'CONSISTE NO EXAME QUE É REALIZADO NO SANGUE RETIRADO POR PUNÇÃO DE UMA ARTÉRIA PARA MEDIR O OXIGÊNIO (O2), O GÁS CARBÔNICO (CO2) E O PH NO SANGUE ARTERIAL ANTES QUE HAJA O CONTATO COM OS TECIDOS CORPORAIS. AVALIA O EQUILÍBRIO ÁCIDO BÁSICO DO PACIENTE, EXPRESSANDO PRINCIPALMENTE SE OS PULMÕES ESTÃO SENDO CAPAZES DE FAZER CORRETAMENTE A TROCA DO OXIGÊNIO PELO DIÓXIDO DE CARBONO NOS ALVÉOLOS, AJUDANDO A DETERMINAR SE O PACIENTE TEM OU NÃO NECESSIDADE DE SUPLEMENTAÇÃO DE OXIGÊNIO, A AVALIAR A FUNÇÃO RENAL E A MONITORAR DOENÇAS PULMONARES, METABÓLICAS OU RENAIS QUE POSSAM CAUSAR DESEQUILÍBRIO ÁCIDO-BASE OU DIFICULDADES RESPIRATÓRIAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010740', 'A D-XILOSE É UMA PENTOSE ABSORVIDA PELO INTESTINO DELGADO E ELIMINADA PELOS RINS SEM SER METABOLIZADA NO FÍGADO. A PROVA DA D-XILOSE É UTILIZADA NO DIAGNÓSTICO DE MÁ-ABSORÇÃO INTESTINAL, O QUE PERMITE A AVALIAÇÃO DA ABSORÇÃO DE CARBOIDRATOS PELA MUCOSA DO INTESTINO DELGADO. DEVEM SER COLHIDAS DUAS AMOSTRAS. A 1ª AMOSTRA BASAL E A 2ª AMOSTRA UMA OU DUAS HORAS PÓS-ADMINISTRAÇÃO DE D-XILOSE POR VIA ORAL, DE ACORDO COM A SOLICITAÇÃO MÉDICA. É REALIZADA A DOSAGEM NO PLASMA PELO MÉTODO ESPECTROFOTOMÉTRICO', '202507', 1);
