INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202010791', 'O BIOMARCADOR PEPTÍDEO NATRIURÉTICO TIPO B (BNP) E SUA PRÓ-MOLÉCULA (NT-PROBNP) SÃO MEMBROS DE UMA CLASSE DE HORMÔNIOS QUE REGULAM A PRESSÃO ARTERIAL. O BNP E O NT-PROBNP SÉRICOS ELEVAM-SE CONTINUAMENTE NA MEDIDA EM QUE A INSUFICIÊNCIA CARDÍACA (IC) AVANÇA. A TESTAGEM DO BNP E DO NT-PROBNP SÉRICO OFERECE UMA MEDIDA NÃO-INVASIVA E OBJETIVA PARA A AVALIAÇÃO DE PACIENTES SOB RISCO DE IC. OS EXAMES LABORATORIAIS DEVEM SER REALIZADOS NO SANGUE PERIFÉRICO DE PACIENTES COM SUSPEITA CLÍNICA DE IC, CONFORME DIRETRIZES DO MINISTÉRIO DA SAÚDE, UNICAMENTE COM A FINALIDADE DE DIAGNÓSTICO AMBULATORIAL EM PACIENTES COM BAIXO E MÉDIO RISCO CLÍNICO, NA ATENÇÃO PRIMÁRIA À SAÚDE (APS) E PARA INDICAÇÃO DE TRATAMENTO COM SARCUBITRIL/VALSARTANA, CONFORME DIRETRIZES DO MINISTÉRIO DA SAÚDE.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020010', 'A CITOQUIMICA HEMATOLOGICA DETECTA A PRESENÇA DE ENZIMAS INTRACELULARES, QUE CATALISAM E CONTROLAM VÁRIAS REAÇÕES, BEM COMO SUBSTÂNCIAS ESPECÍFICAS PARA UM DETERMINADO TIPO CELULAR. A ATIVIDADE DESSAS ENZIMAS É DEMONSTRADA PELA ADIÇÃO DE PSEUDO-SUBSTRATOS, QUE LEVAM A UMA CONVERSÃO ENZIMÁTICA COLORIDA PERCEPTÍVEIS A MICROSCOPIA OPTICA.  AS CÉLULAS SANGÜÍNEAS NORMAIS, ASSIM COMO SUAS PRECURSORAS, TÊM ATIVIDADE E LOCALIZAÇÃO ENZIMÁTICAS DIFERENTES QUANDO COMPARADAS A SEUS CORRESPONDENTES MALIGNOS. A CITOQUÍMICA BASEIA-SE NA RELAÇÃO DA MORFOLOGIA COM A DISTRIBUIÇÃO, A INTENSIDADE E A LOCALIZAÇÃO DA REAÇÃO NA CÉLULA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020029', 'A CONTAGEM DE PLAQUETAS CONSISTE NA AVALIAÇÃO QUANTITATIVA DESTE ELEMENTO DO SANGUE.   É REALIZADA PELA CONTAGEM ELETRÔNICA EM APARELHO AUTOMÁTICO OU PELA CONTAGEM NO MICROSCÓPICO EM CÂMARAS ESPECÍFICAS. PERMMITE REALIZAR O DIAGNÓSTICO DE HIPERPLAQUETENIA E PLAQUETOPENIA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020037', 'A CONTAGEM DE RETICULÓCITOS É ÚTIL PARA AVALIAR A ATIVIDADE ERITROPOIÉTICA, SENDO IMPORTANTE PARA O DIAGNÓSTICO DIFERENCIAL DAS ANEMIAS, ASSIM COMO PARA ACOMPANHAR O TRATAMENTO. VALORES AUMENTADOS SÃO ENCONTRADOS NA HIPERATIVIDADE DA MEDULA ÓSSEA (RETICULOCITOSE), COMO, POR EXEMPLO, NAS ANEMIAS HEMOLÍTICAS. VALORES DIMINUÍDOS SÃO ENCONTRADOS NA HIPOATIVIDADE DA MEDULA ÓSSEA (RETICULOCITOPENIA), COMO, POR EXEMPLO, NA APLASIA MEDULAR.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020045', 'A DETERMINAÇÃO DE CURVA DE RESISTÊNCIA GLOBULAR AVALIA A HABILIDADE DOS GLÓBULOS VERMELHOS DE INCORPORAR ÁGUA EM SEU INTERIOR, SEM QUE OCORRA LISE DA CÉLULA. ESSA RESISTÊNCIA ESTÁ ASSOCIADA À RELAÇÃO ENTRE SUPERFÍCIE/VOLUME DO GLÓBULO.', '202507', 1);
