INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020061', 'AS SULFONAMIDAS, ASSIM COMO A FENACETINA E A ACETANILIDA, PODEM ALTERAR A HEMOGLOBINA PELA ADIÇÃO DE UM RADICAL SULFUROSO À SUA MOLÉCULA, O QUE ORIGINA A SULFOHEMOGLOBINA, A QUAL NÃO TEM CAPACIDADE DE TRANSPORTAR OXIGÊNIO. ESSA ALTERAÇÃO DA HEMOGLOBINA NÃO PODE SER REVERTIDA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020070', 'A DETERMINAÇÃO DE TEMPO DE COAGULAÇÃO AVALIA A ATIVIDADE DOS FATORES QUE PARTICIPAM DO PROCESSO INTRÍNSECO DA COAGULAÇÃO. RESULTADOS NORMAIS, NO ENTANTO, PODEM SER OBTIDOS EM PACIENTES COM DEFICIÊNCIA LEVE OU MODERADA DE UM DESSES FATORES. O RESULTADO MOSTRA O TEMPO GASTO PARA A HOMEOSTASIA COMPLETA DO SANGUE QUANDO COLHIDO E COLOCADO EM CONDIÇÕES PADRÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020088', 'A DETERMINAÇÃO DE TEMPO DE LISE DA EUGLOBINA CONSISTE NO TESTE REALIZADO COM O PLASMA, APÓS ELE SER ACIDIFICADO, DILUÍDO, REFRIGERADO E PRECIPITADO. A FRAÇÃO PRECIPITADA (FRACÃO DAS EUGLOBINAS) CONTÉM FIBRINOGÊNIO, PLASMINOGÊNIO, PLASMINA ATIVA E ATIVADORES E INIBIDORES DO PLASMINOGÊNIO E, POR CENTRIFUGAÇÃO, É REDISSOLVIDA. ADICIONA-SE, EM SEGUIDA, A TROMBINA QUE INDUZ A FORMAÇÃO DO COÁGULO E REGISTRA-SE O TEMPO ATÉ A LISE. OS RESULTADOS DESSE TESTE REFLETEM A CAPACIDADE FIBRINOLÍTICA E A SUA INIBIÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020096', 'A DETERMINAÇÃO DO TEMPO DE SANGRAMENTO AVALIA A INTERAÇÃO DA PLAQUETA COM A PAREDE DO VASO SANGUÍNEO E A FORMAÇÃO SUBSEQUENTE DO COÁGULO HEMOSTÁTICO DE MODO INDEPENDENTE DA CASCATA DA COAGULAÇÃO. EXISTE UMA RELAÇÃO QUASE LINEAR ENTRE A CONTAGEM DE PLAQUETAS E O TEMPO DE SANGRAMENTO. PODE SER REALIZADO PELA TÉCNICA DE DUKE, A QUAL É FEITA ATRAVÉS DE UMA PUNÇÃO NO LOBO DA ORELHA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020100', 'A DETERMINAÇÃO DO TEMPO DE SANGRAMENTO AVALIA A INTERAÇÃO DA PLAQUETA COM A PAREDE DO VASO SANGUÍNEO E A FORMAÇÃO SUBSEQUENTE DO COÁGULO HEMOSTÁTICO DE MODO INDEPENDENTE DA CASCATA DA COAGULAÇÃO. EXISTE UMA RELAÇÃO QUASE LINEAR ENTRE A CONTAGEM DE PLAQUETAS E O TEMPO DE SANGRAMENTO. PODE SER REALIZADO PELA TÉCNICA DE IVY. APÓS INSUFLAÇÃO DE UM ESFIGMOMANÔMETRO COLOCADO NO BRAÇO ATÉ AOS 40 MM HG. UMA LANCETA, AGULHA OU BISTURI É USADO PARA PROVOCAR UMA FERIDA PERFUROCORTANTE DE COMPRIMENTO E PROFUNDIDADE PADRONIZADOS NO ANTEBRAÇO DO MESMO LADO E MEDE-SE  O TEMPO NECESSÁRIO PARA CESSAR A HEMORRAGIA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020118', 'CONSISTE NO EXAME DE SANGUE PERIFÉRICO QUE AVALIA O TEMPO DE VIDA MÉDIA DAS HEMÁCIAS E QUE EM MÉDIA É DE 120 DIAS. AVALIA DOENÇAS DO SANGUE QUE REDUZEM ESSE TEMPO MÉDIO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020126', 'A DETERMINAÇÃO DE TEMPO DE TROMBINA É UM TESTE DE SCREENING PARA DETECTAR DEFICIÊNCIA DE  FIBRINOGÊNIO FUNCIONAL . O EXAME SE BASEIA NA MEDIDA DO TEMPO QUE DEMORA EM COAGULAR UM PLASMA CITRATADO, COLOCADO A 37O C E EM PRESENÇA DE UMA SOLUÇÃO DE TROMBINA DE ATIVIDADE FIXA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020134', 'A DETERMINAÇÃO DE TEMPO DE TROMBOPLASTINA PARCIAL ATIVADA (TTP ATIVADA) NORMALMENTE MEDE A VIA INTRÍNSECA DA COAGULAÇÃO. É INDICADA NOS CASOS DE SUSPEITA DE DEFICIÊNCIA DE FATORES DA VIA INTRÍNSECA DA COAGULAÇÃO, ANTES DE SEREM REALIZADAS INTERVENÇÕES CIRÚRGICAS, E NO CONTROLE DE TERAPÊUTICA ANTICOAGULANTE PELA HEPARINA. A DETERMINAÇÃO DE TEMPO DE TROMBOPLASTINA PARCIAL ATIVADA (TTP ATIVADA) NORMALMENTE MEDE A VIA INTRÍNSECA DA COAGULAÇÃO. É INDICADA NOS CASOS DE SUSPEITA DE DEFICIÊNCIA DE FATORES DA VIA INTRÍNSECA DA COAGULAÇÃO, ANTES DE SEREM REALIZADAS INTERVENÇÕES CIRÚRGICAS, E NO CONTROLE DE TERAPÊUTICA ANTICOAGULANTE PELA HEPARINA. O TEMPO DE TROMBOPLASTINA PARCIAL CORRESPONDE AO TEMPO GASTO PARA OCORRER A COAGULAÇÃO DO PLASMA RECALCIFICADO EM PRESENÇA DE CEFALINA', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020142', 'O TEMPO DA PROTROMBINA ESTÁ PROLONGADO NAS SEGUINTES CONDIÇÕES: NAS DEFICIÊNCIAS DE FATORES VII, V, X, II (PROTROMBINA) E I, NA PRESENÇA DE ALGUNS TIPOS DE ANTICOAGULANTES CIRCULANTES, EM PACIENTES COM DOENÇA HEPÁTICA GRAVE, EM CONDIÇÕES QUE ALTEREM A ABSORÇÃO, SÍNTESE E O METABOLISMO DA VITAMINA K E EM PACIENTES COM HIPOFIBRINOGENEMIA. MÉTODO: COAGULOMÉTRICO EM SANGUE VENOSO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020150', 'A VELOCIDADE DE HEMOSSEDIMENTAÇÃO (VHS) É UM FENÔMENO NÃO ESPECÍFICO. ALÉM DISSO SUA DETERMINAÇÃO É CLINICAMENTE ÚTIL NAS DESORDENS ASSOCIADAS COM A PRODUÇÃO AUMENTADA DE PROTEÍNAS DE FASE AGUDA, EMBORA NÃO SEJA ESPECÍFICO. NA ARTRITE REUMATÓIDE E NA TUBERCULOSE, POR EXEMPLO, É UM ÍNDICE DE PROGRESSÃO DA DOENÇA.', '202507', 1);
