INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020177', 'CONSISTE NO EXAME QUE DOSA A ANTITROMBINA AT E FAZ PARTE DE UM CONJUNTO DE EXAMES PARA A INVESTIGAÇÃO DE TROMBOFILIA. A ANTITROMBINA É UM ANTICOAGULANTE NATURAL, COFATOR DA HEPARINA, QUE INIBE A TROMBINA E O FATOR X ATIVADO EM CONCENTRAÇÕES FISIOLÓGICAS. É INDICADO PARA A INVESTIGAÇÃO DE DEFICIÊNCIA DE AT, CONGÊNITA OU ADQUIRIDA, ASSOCIADA A MANIFESTAÇÕES TROMBÓTICAS VENOSAS.', '202507', 1);
