INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0202020452', 'TÉCNICA BASEADA NA VISUALIZAÇÃO DO PARASITO,ATRAVÉS DE MICROSCOPIA ÓTICA E QUE PERMITE A DIFERENCIAÇÃOESPECÍFICA DOS PARASITOS A PARTIR DA ANÁLISE DE SUA COLORAÇÃO.POSSIBILITA O ACOMPANHAMENTO NA LÂMINA DE VERIFICAÇÃO DECURA E DA REDUÇÃO PROGRESSIVA DA PARASITEMIA, DA EFICÁCIA DOTRATAMENTO E A IDENTIFICAÇÃO OPORTUNA DE RECAÍDAS DAMALÁRIA.', '202507', 1);
