INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050014', 'CONSISTE NO EXAME RADIOLÓGICO DIAGNÓSTICO CUJO OBJETIVO É AVALIAR O FUNCIONAMENTO E A FORMA DO INTESTINO GROSSO (CECO, CÓLON ASCENDENTE, CÓLON TRANSVERSO, CÓLON DESCENDENTE, CÓLON SIGMOIDE, RETO E CANAL ANAL), USANDO CONTRASTE DE BÁRIO E DUPLO CONTRASTE. TAMBÉM CHAMADO CLISTER OPACO CONSISTE EM COLOCAR ATRAVÉS DE UMA SONDA, UM POUCO DE CONTRASTE (GERALMENTE DE BÁRIO) NO INTESTINO DO INDIVÍDUO E EM SEGUIDA REALIZAR UM RAIO-X ABDOMINAL PARA INVESTIGAR POSSÍVEIS DOENÇAS OU ALTERAÇÕES NO INTESTINO. É NECESSÁRIA PREPARAÇÃO A PARTIR DE DOIS DIAS ANTES DA REALIZAÇÃO COM RESTRIÇÃO DE DETERMINADOS TIPOS DE ALIMENTOS E A ADMINISTRAÇÃO DE LAXANTE, PARA QUE SE ELIMINE A MAIOR QUANTIDADE DE FEZES POSSÍVEL, MELHORANDO A QUALIDADE DO RESULTADO DO EXAME. ATUALMENTE É COMUM A SUA SUBSTITUIÇÃO COLONOSCOPIA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050022', 'A COLANGIOGRAFIA É O EXAME DE RAIO X REALIZADO DURANTE A COLECISTECTOMIA, É DE GRANDE VALOR NA EXPLORAÇÃO DA VIA BILIAR PRINCIPAL, SENDO UM TEMPO IMPORTANTE DA CIRURGIA DAS VIAS BILIARES, POIS POSSIBILITA A AVALIAÇÃO ANATÔMICA DE TODA A ÁRVORE BILIAR, DESDE OS DUCTOS INTRA-HEPÁTICOS ATÉ O COLÉDOCO TERMINAL, IDENTIFICA AS VARIAÇÕES ANATÔMICAS, AVALIA A PERVIEDADE DA PAPILA, ALTERAÇÕES INFLAMATÓRIAS, ESTENOSES BENIGNAS OU MALIGNAS, DIVERTÍCULO DE DUODENO, PERMITE A  IDENTIFICAÇÃO DE CÁLCULOS SILENCIOSOS NAS VIAS BILIARES, EVITA EXPLORAÇÕES DESNECESSÁRIAS DO COLÉDOCO, DETERMINA O ESTADO FUNCIONAL DA AMPOLA HEPATO-PANCREÁTICA, REVELA COLELITOS NÃO DETECTADOS PREVIAMENTE E PEQUENAS LESÕES, ESTREITAMENTOS OU DILATAÇÕES DENTRO DOS DUETOS BILIARES.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050030', 'CONSISTE NO PROCEDIMENTO PELO QUAL É ADMINISTRADO UM MEIO DE CONTRASTE HIDROSSOLÚVEL NAS VIAS BILIARES APÓS PUNÇÃO E PINÇAMENTO DO DRENO DE KEHR, POSICIONADO NO INTRA-OPERATÓRIO, PERMITINDO VERIFICAR A POSSÍVEL PRESENÇA DE CÁLCULOS INTRA-HEPÁTICOS OU RESIDUAIS NO HEPATO COLÉDOCO, AVALIANDO A PERMEABILIDADE DAS VIAS BILIARES INCLUINDO A PUPILA DE VATER.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050049', 'CONSISTE NA TÉCNICA ANTIGA DE DIAGNÓSTICO RADIOLÓGICO ATUALMENTE RARAMENTE INDICADO, É UM PROCEDIMENTO ÚTIL EM PACIENTES COM ICTERÍCIA OBSTRUTIVA DEVIDO A NEOPLASIA PROVÁVEL DA ARTICULAÇÃO DUODENAL. SUA PRINCIPAL UTILIDADE É ESPECIFICAR A ANATOMIA E ETIOLOGIA DO PROCESSO PATOLÓGICO NESSES CASOS EM QUE OS OUTROS MÉTODOS DE DIAGNÓSTICO POR IMAGEM NÃO SÃO CONCLUSIVOS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050057', 'CONSISTE DO EXAME DE RAIO X REALIZADO APÓS A CATETERIZAÇÃO DO ORIFÍCIO FISTULOSO E INJEÇÃO DE CONTRASTE IODADO PELO MESMO, PARA OPACIFICAR UM TRAJETO FISTULOSO CUTÂNEO OU MUCOSO E REALIZAR IMAGENS COM A RADIOLOGIA CONVENCIONAL. A FISTULOGRAFIA MOSTRA SE O TRAJETO FISTULOSO TEM OU NÃO COMUNICAÇÃO COM A ALÇA INTESTINAL.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050065', 'CONSISTE NO EXAME GINECOLÓGICO DE RAIO-X DO ÚTERO E DAS TROMPAS, FEITO COM CONTRASTE, COM O OBJETIVO DE AVALIAR AS CAUSAS DE INFERTILIDADE DE UM CASAL. É CAPAZ DE IDENTIFICAR PROBLEMAS GINECOLÓGICOS, VISUALIZA A ANATOMIA DO SISTEMA REPRODUTOR FEMININO DESDE O ÚTERO ATÉ OS OVÁRIOS. IDENTIFICA ANOMALIAS NO ÚTERO OU NAS TROMPAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050073', 'CONSISTE NO EXAME DO SISTEMA URINÁRIO, NO QUAL O MEIO DE CONTRASTE É INTRODUZIDO POR VIA PERCUTÂNEA PERMITINDO OBTER UMA IMAGEM CLARA DO URETER, PELVE RENAL E CÁLICES GRAÇAS AO USO DE SUBSTÂNCIAS GASOSAS (OXIGÊNIO E DIÓXIDO DE CARBONO) E OS CONTRASTES LÍQUIDOS. É REALIZADA APENAS POR UM LADO, UMA VEZ QUE O ESTUDO SIMULTÂNEO DE AMBOS OS URETERES PROVOCA ESPASMOS PELVE E COPOS, SENDO DE DIFÍCIL TOLERÂNCIA. PARA A ADMINISTRAÇÃO DE CONTRASTE É USADO UM CATETER ESPECIAL, SENDO O DIÂMETRO SELECIONADO DEPENDENDO DO GRAU DE ESTREITAMENTO DO URETER.', '202507', 1);
