INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0204050154', 'ESTUDAR AS ESTRUTURAS DO DUODENO, JEJUNO E ÍLEO. O EXAME DO INTESTINO DELGADO TEM CONSISTE NO PROCEDIMENTO QUE AVALIA TODOS OS SEGMENTOS DO INTESTINO DELGADO, INCLUINDO VÁLVULA ILEOCECAL, ATÉ O INÍCIO DO GROSSO. PODE SER REALIZADO PARA AVALIAR A MORFOLOGIA DO INTESTINO E A SUA FUNCIONALIDADE. ESTE EXAME É INDICADO EM CASO DE DOENÇA INFLAMATÓRIA INTESTINAL (DOENÇA DE CHRON, RETOCOLITE ULCERATIVA), DIARREIA E CONSTIPAÇÃO. O EXAME É REALIZADO POR MEIO DO USO DE FLUOROSCOPIA E UM AGENTE DE CONTRASTE (BÁRIO) TOMADO POR VIA ORAL. O TEMPO DE EXECUÇÃO VARIÁVEL, CONFORME OS MOVIMENTOS INTESTINAIS (PERISTALTISMO), ÀS VEZES PODENDO DEMORAR VÁRIAS HORAS.', '202507', 1);
