INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205010016', 'CONSISTE NO ECOCARDIOGRAMA QUE É FEITO COMO PARTE DO TESTE DE ESFORÇO, DURANTE O QUAL, O PACIENTE SE EXERCITA OU LHE É ADMINISTRADO UM MEDICAMENTO PARA OBRIGAR QUE O CORAÇÃO BATA MAIS FORTE E RÁPIDO, JÁ QUE ALGUNS PROBLEMAS CARDÍACOS, COMO DOENÇA NA ARTÉRIA CORONÁRIA, SÃO MAIS FACILMENTE DIAGNOSTICADOS QUANDO O CORAÇÃO ESTÁ BATENDO MAIS FORTE E RÁPIDO. É UM PROCEDIMENTO NÃO INVASIVO E ALTAMENTE PRECISO ONDE É REALIZADA AVALIAÇÃO DAS ESTRUTURAS E DO FUNCIONAMENTO DO CORAÇÃO POR MEIO DE ULTRASSOM. PODE SER REALIZADO DE DUAS MANEIRAS: SOB ESTRESSE FARMACOLÓGICO OU SOB ESFORÇO FÍSICO. NA FORMA FARMACOLÓGICA SÃO ADMINISTRADAS DROGAS ENDOVENOSAS (DOBUTAMINA OU DIPIRIDAMOL OU ADENOSINA, ASSOCIADAS OU NÃO A ATROPINA), QUE AUMENTAM A DEMANDA DE OXIGÊNIO DO CORAÇÃO, PERMITINDO DIAGNOSTICAR UMA DEFICIÊNCIA NA CONTRATILIDADE REGIONAL DO MÚSCULO CARDÍACO (MIOCÁRDIO), DECORRENTE DE UMA INADEQUADA PERFUSÃO SANGUÍNEA, GERALMENTE CONSEQUENTE A UMA OBSTRUÇÃO NAS ARTÉRIAS CORONÁRIAS. TEM INDICAÇÃO ESPECÍFICA EM PACIENTES COM HIPERTENSÃO ARTERIAL NÃO CONTROLADA, INSUFICIÊNCIA CARDÍACA DESCOMPENSADA, INFECÇÃO ATIVA, ARRITMIAS COMPLEXAS NÃO CONTROLADAS, NA GRAVIDEZ DE RISCO, DOENÇA ESTENÓTICA VALVAR IMPORTANTE, ENTRE OUTRAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205010024', 'CONSISTE NO PROCEDIMENTO NÃO INVASIVO E ALTAMENTE PRECISO ONDE É REALIZADA AVALIAÇÃO DAS ESTRUTURAS E DO FUNCIONAMENTO DO CORAÇÃO POR MEIO DE ULTRASSOM. AS IMAGENS SÃO OBTIDAS POR MEIO DE UM TRANSDUTOR PRESENTE NA EXTREMIDADE DA SONDA INTRODUZIDA NO ESÔFAGO DO PACIENTE, POSSIBILITANDO UMA MELHOR IMAGEM DE CERTAS ESTRUTURAS CARDÍACAS, COMO POR EXEMPLO, O APÊNDICE ATRIAL ESQUERDO, O SEPTO INTERATRIAL E AS VEIAS PULMONARES, ALÉM DO QUE JÁ É IDENTIFICADO PELO ECOCARDIOGRAMA TRANSTORÁCICO. A ECOCARDIOGRAFIA APRESENTA IMAGENS ESTÁTICAS E EM MOVIMENTO DO MÚSCULO E DAS VALVAS CARDÍACAS E ATRAVÉS DO MAPEAMENTO DE FLUXOS EM CORES PELA TÉCNICA DOPPLER, IDENTIFICA A DIREÇÃO E VELOCIDADE DO FLUXO SANGUÍNEO NO INTERIOR DAS CAVIDADES CARDÍACAS. O DOPPLER PODE SER PULSADO, CONTÍNUO E COLORIDO. É A MAIS REALIZADA DE TODAS AS MODALIDADES DE ECOCARDIOGRAMA. O DOPPLER PULSADO ANALISA A VELOCIDADE DO FLUXO SANGUÍNEO EM UM DETERMINADO PONTO ESPECÍFICO DO CORAÇÃO, COM UM ESPECTRO DE VELOCIDADE LIMITADO. O DOPPLER CONTÍNUO ANALISA O SOMATÓRIO DAS VELOCIDADES DE TODOS OS FLUXOS EM UMA DETERMINADA FAIXA DO CORAÇÃO ONDE É POSICIONADO O CURSOR, E PERMITE REGISTRAR O FLUXO EM ALTAS VELOCIDADES. O DOPPLER COLORIDO OU MAPEAMENTO DE FLUXO A CORES ANALISA O FLUXO SANGUÍNEO EM DUAS DIMENSÕES E AS CORES DETERMINAM A SUA DIREÇÃO DENTRO DAS CAVIDADES CARDÍACAS. TEM IMPACTO NA AVALIAÇÃO DE PACIENTES COM PATOLOGIAS ESOFAGIANAS COMO ESTENOSE OU MEGAESÔFAGO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205010032', 'CONSISTE NO PROCEDIMENTO NÃO INVASIVO E ALTAMENTE PRECISO ONDE É REALIZADA AVALIAÇÃO DAS ESTRUTURAS E DO FUNCIONAMENTO DO CORAÇÃO POR MEIO DE ULTRASSOM. O TRANSDUTOR (SONDA) É COLOCADO SOBRE O TÓRAX DO PACIENTE E É CAPAZ DE DETECTAR SOPROS CARDÍACOS, IDENTIFICAR CAUSAS DE PALPITAÇÃO, SÍNCOPE, FALTA DE AR, DOR TORÁCICA OU DOENÇAS DO MÚSCULO CARDÍACO (INFARTO DO MIOCÁRDIO, MIOCARDIOPATIAS), INSUFICIÊNCIA CARDÍACA, VALVULOPATIAS, ANOMALIAS CONGÊNITAS, ENTRE OUTRAS. A ECOCARDIOGRAFIA APRESENTA IMAGENS ESTÁTICAS E EM MOVIMENTO DO MÚSCULO E DAS VALVAS CARDÍACAS E ATRAVÉS DO MAPEAMENTO DE FLUXOS EM CORES PELA TÉCNICA DOPPLER, IDENTIFICA A DIREÇÃO E VELOCIDADE DO FLUXO SANGUÍNEO NO INTERIOR DAS CAVIDADES CARDÍACAS. O DOPPLER PODE SER PULSADO, CONTÍNUO E COLORIDO. É A MAIS COMUM DE TODAS AS MODALIDADES DE ECOCARDIOGRAMA. O DOPPLER PULSADO ANALISA A VELOCIDADE DO FLUXO SANGUÍNEO EM UM DETERMINADO PONTO ESPECÍFICO DO CORAÇÃO, COM UM ESPECTRO DE VELOCIDADE LIMITADO. O DOPPLER CONTÍNUO ANALISA O SOMATÓRIO DAS VELOCIDADES DE TODOS OS FLUXOS EM UMA DETERMINADA FAIXA DO CORAÇÃO ONDE É POSICIONADO O CURSOR, E PERMITE REGISTRAR O FLUXO EM ALTAS VELOCIDADES. O DOPPLER COLORIDO OU MAPEAMENTO DE FLUXO A CORES ANALISA O FLUXO SANGUÍNEO EM DUAS DIMENSÕES E AS CORES DETERMINAM A SUA DIREÇÃO DENTRO DAS CAVIDADES CARDÍACAS. PERMITE OBTER INFORMAÇÃO MORFOLÓGICA E FUNCIONAL SOBRE AS CÂMARAS (AURÍCULAS E VENTRÍCULOS), VÁLVULAS E PAREDES CARDÍACAS, REALIZADO COM O PACIENTE EM SITUAÇÃO DE REPOUSO, DEITADO SOBRE O LADO ESQUERDO EM CONDIÇÕES QUE NÃO IMPÕEM QUALQUER ESFORÇO AO CORAÇÃO, AVALIA A MORFOLOGIA E A FUNÇÃO CARDÍACAS E, ADICIONALMENTE O FLUXO SANGUÍNEO E O MOVIMENTO DOS TECIDOS CARDÍACOS. DURANTE A REALIZAÇÃO EXAME O TRANSDUTOR É MOVIMENTADO SOBRE A PAREDE TORÁCICA, PODENDO SER NECESSÁRIO REALIZAR ALGUMA PRESSÃO; PODE TAMBÉM SER SOLICITADO AO PACIENTE QUE MUDE DE POSIÇÃO OU QUE REALIZE MOVIMENTOS RESPIRATÓRIOS ESPECÍFICOS, NÃO ENVOLVE RADIAÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205010040', 'É O MÉTODO MAIS INTEGRADO E PRECISO NO DIAGNÓSTICO DE DIVERSAS PATOLOGIAS VASCULARES. PODE SER FEITO NAS PERNAS, BRAÇOS, PESCOÇO, ABDÔMEN, VASOS UMBILICAIS E PLACENTA DURANTE A GESTAÇÃO. ANALISA AS CARACTERÍSTICAS DO FLUXO SANGUÍNEO EM ARTÉRIAS E VEIAS NO DIAGNÓSTICO DE DOENÇAS VASCULARES PERIFÉRICAS E DE ÓRGÃOS ABDOMINAIS. DISPONIBILIZA INFORMAÇÕES SOBRE A VELOCIDADE DE DETERMINADO FLUXO, E MOSTRA A DIREÇÃO E A MAGNITUDE DESSA VELOCIDADE. PERMITE MAPEAR EM CORES OS VASOS SANGUÍNEOS DE UMA REGIÃO ANATÔMICA E TORNA POSSÍVEL A IDENTIFICAÇÃO DE DIMINUTOS VASOS QUE NÃO SERIAM VISUALIZADOS PELA ESCALA DE CINZA. A CODIFICAÇÃO DA FREQUÊNCIA MÉDIA DO FLUXO É TRADUZIDA EM DUAS CORES DOMINANTES (VERMELHO PARA AS CORRENTES QUE SE APROXIMAM DA SONDA E AZUL PARA AS QUE SE AFASTAM), E AS TONALIDADES DIFERENTES REPRESENTAM VELOCIDADES DIFERENTES. VARIAÇÃO NAS VELOCIDADES, AS QUAIS PODEM SER VISTAS EM ÁREAS DE TURBULÊNCIA, PODE SER REPRESENTADA POR CORES MAIS CLARAS (AMARELO E VERDE), E QUANTO MAIOR A VELOCIDADE, MAIS CLARA É A TONALIDADE DA COR. O MAPEAMENTO DE FLUXO A CORES ANALISA O FLUXO SANGUÍNEO EM DUAS DIMENSÕES E AS CORES DETERMINAM A SUA DIREÇÃO DENTRO DAS VEIAS E ARTÉRIAS. PERMITE A INVESTIGAÇÃO DETALHADA E NÃO INVASIVA DA HEMODINÂMICA CORPORAL, QUANTITATIVA E QUALITATIVAMENTE DO PONTO DE VISTA MORFOLÓGICO (ÓRGÃO E SUAS PARTES) E FUNCIONAL. PODE SER FEITO EM MULHERES GRÁVIDAS SEM NENHUM PREJUÍZO AO FETO, E NÃO UTILIZA IRRADIAÇÕES. PARA EFEITO DE REGISTRO NO SISTEMA DE INFORMAÇÃO HOSPITALAR, O PROCEDIMENTO PODE TER A QUANTIDADE MÁXIMA DE 5 PROCEDIMENTOS REALIZADOS EM UMA AIH. NO CASO DE O PACIENTE NECESSITAR DE REALIZAR MAIS DE 5 PROCEDIMENTOS NA MESMA INTERNAÇÃO, O GESTOR PODE AUTORIZAR O REGISTRO DE MAIS DE 5 PROCEDIMENTOS. NO CASO DO SISTEMA DE INFORMAÇÃO AMBULATORIAL PODE INFORMAR NO BPA/I ATÉ 5 PROCEDIMENTOS PARA O MESMO PACIENTE NA MESMA COMPETÊNCIA. ESTAS QUANTIDADES DE PROCEDIMENTO REALIZADO, INDEPENDEM DA QUANTIDADE DE VASOS ESTUDADOS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205010059', 'CONSISTE EM PROCEDIMENTO NÃO INVASIVO PARA AVALIAÇÃO DA VELOCIDADE DO FLUXO SANGUÍNEO NO ÚTERO E FETO PLACENTÁRIO PELO DOPPLER. POR MEIO DE SISTEMA DE MAPEAMENTO COLORIDO DO FLUXO DE SANGUE EM ALGUNS VASOS MATERNO E FETAIS É POSSÍVEL AVALIAR O PROGNÓSTICO DA GESTAÇÃO E AS CONDIÇÕES DO FETO QUANTO À OXIGENAÇÃO E SE A PLACENTA EXIBE ALGUM SINAL DE INSUFICIÊNCIA. ATRAVÉS DA ANÁLISE DO FLUXO DAS ARTÉRIAS UTERINAS, PODE SER AVALIADO O RISCO DE A GESTANTE DESENVOLVER QUADRO DE PRÉ-ECLÂMPSIA. É REALIZADO POR VIA ABDOMINAL E PERMITE TAMBÉM AVALIAR A IDADE GESTACIONAL, NÚMERO DE FETOS, ANATOMIA FETAL (DE FORMA MAIS SUCINTA QUE OS EXAMES MORFOLÓGICOS), LOCALIZAÇÃO DA PLACENTA, QUANTIDADE DE LÍQUIDO AMNIÓTICO, SEXO FETAL, PESO ESTIMADO DO FETO, POSIÇÃO DO FETO NO ÚTERO E O BEM ESTAR FETAL. TAMBÉM É FEITA A AVALIAÇÃO DE VASOS FETAIS PRINCIPALMENTE AS ARTÉRIAS UMBILICAIS E ARTÉRIA CEREBRAL MÉDIA, COM O INTUITO DE VERIFICAR O FUNCIONAMENTO DA PLACENTA E SE O FETO ENCONTRA-SE BEM OXIGENADO.INCLUINDO AS GESTAÇÕES MÚLTIPLAS.', '202507', 1);
