INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205020038', 'É O EXAME POR ULTRASSOM QUE POSSIBILITA DETECTAR LESÕES PEQUENAS NO PARÊNQUIMA HEPÁTICO E DILATAÇÕES NAS VIAS BILIARES. MOSTRA COM PRECISÃO A VESÍCULA BILIAR E AS CONDIÇÕES DA PAREDE, BEM COMO A DILATAÇÃO DO COLÉDOCO E PRESENÇA OU NÃO DE CÁLCULOS. NO PÂNCREAS POSSIBILITA DETECÇÃO DE PROCESSOS INFLAMATÓRIOS AGUDOS E CRÔNICOS, CISTOS E PSEUDOCISTOS, TUMORES, ANOMALIAS CONGÊNITAS E TRAUMATISMO. IDENTIFICA TUMORES ABDOMINAIS DE CONTEÚDO LÍQUIDO OU SÓLIDO, BEM COMO A PRESENÇA DE METÁSTASES HEPÁTICAS OU ESPLÊNICAS E O ANEURISMA DE AORTA, E ESTUDAR A VEIA CAVA INFERIOR EM TODA A SUA EXTENSÃO NESTA CAVIDADE. NO RETROPERITÔNIO IDENTIFICA LESÕES SÓLIDAS OU COLEÇÕES LÍQUIDAS. NO SISTEMA URINÁRIO PERMITE IDENTIFICAR OS DIFERENTES TIPOS DE TUMORES, HIDRONEFROSE E DOENÇAS POLICÍSTICAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205020046', 'CONSISTE EM PROCEDIMENTO NÃO INVASIVO, UTILIZADO PARA AVALIAÇÃO, SEGUIMENTO, DIAGNÓSTICO E CARACTERIZAÇÃO DAS ALTERAÇÕES DO FÍGADO, DA VESÍCULA BILIAR, DOS RINS, DO PÂNCREAS, DA BEXIGA, DOS GRANDES VASOS, DO RETROPERITÔNIO E, EVENTUALMENTE, DO TRATO GASTROINTESTINAL. NÃO UTILIZA NENHUM TIPO DE RADIAÇÃO E NÃO APRESENTA EFEITOS COLATERAIS. ALÉM DO JEJUM NECESSÁRIO PARA EVITAR QUE A VESÍCULA BILIAR SE ESVAZIE E PERMITA AVALIAR DE FORMA ADEQUADA ÓRGÃOS MAIS PROFUNDOS. DEVE SER REALIZADO COM A BEXIGA CHEIA PARA DESLOCAR AS ALÇAS INTESTINAIS ATUANDO COMO UMA JANELA PARA A TRANSMISSÃO DAS ONDAS ULTRASSÔNICAS, FACILITANDO A VISUALIZAÇÃO E AVALIAÇÃO DOS ÓRGÃOS E ESTRUTURAS DA REGIÃO ABDOMINAL.', '202507', 1);
