INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205020143', 'PERMITE O DIAGNÓSTICO DE GRAVIDEZ, DA VIABILIDADE DA GRAVIDEZ, A DETERMINAÇÃO DA IDADE GESTACIONAL E DO TAMANHO DO FETO, ASSIM COMO O DIAGNÓSTICO DE MALFORMAÇÕES FETAIS. AUXILIAR O ACOMPANHAMENTO DO CRESCIMENTO DO FETO, O PLANEJAMENTO DOS EXAMES PRÉ-NATAIS E A PREVISÃO DA DATA DO PARTO. NELE SÃO REALIZADAS MEDIDAS DO BEBÊ, AVALIAÇÃO DOS ÓRGÃOS INTERNOS DO FETO, DA PLACENTA E DA QUANTIDADE DE LÍQUIDO AMNIÓTICO. INCLUINDO AS GESTAÇÕES MÚLTIPLAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205020151', 'AVALIA O CRESCIMENTO E VITALIDADE FETAL COM A UTILIZAÇÃO DO RECURSO DO DOPPLER COLORIDO. PERMITE A ANÁLISE BÁSICA DA ANATOMIA FETAL, A AVALIAÇÃO CARDÍACA DAS ARTÉRIAS UMBILICAIS, DAS ARTÉRIAS RENAIS E INSERÇÃO DO CORDÃO UMBILICAL. INCLUINDO AS GESTAÇÕES MÚLTIPLAS. O DOPPLER PULSADO PERMITE A ANÁLISE DA FUNÇÃO PLACENTÁRIA PELO CÁLCULO DO ÍNDICE DE PULSATILIDADE DA ARTÉRIA UMBILICAL E CEREBRAL MÉDIA. TAMBÉM É POSSÍVEL A AVALIAÇÃO DOS ÍNDICES DE RESISTÊNCIA E PULSATILIDADE DAS ARTÉRIAS UTERINAS QUE PODEM PREDIZER A PRESENÇA DE PRÉ ECLÂMPSIA. O SENTIDO DO FLUXO É CODIFICADO PELAS CORES AZUL E VERMELHA. A IMAGEM EM CORES É SOBREPOSTA À IMAGEM EM ESCALA DE CINZAS NO PLANO BIDIMENSIONAL DA ULTRASSONOGRAFIA EM TEMPO REAL, PERMITINDO IDENTIFICAR O FLUXO E O SENTIDO DO SANGUE.', '202507', 1);
