INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205020178', 'É A TÉCNICA DE ESCOLHA PARA A AVALIAÇÃO ENCEFÁLICA DE NEONATOS E DE LACTENTES, ATÉ O FECHAMENTO DA FONTANELA ANTERIOR. É UM MÉTODO DIAGNÓSTICO IMPORTANTE NO DIAGNÓSTICO E NO SEGUIMENTO DE HEMORRAGIAS INTRACRANIANAS E LESÕES HIPÓXICO-ISQUÊMICAS, NO DIAGNÓSTICO DE MALFORMAÇÕES CONGÊNITAS ENCEFÁLICAS, INFECÇÕES CONGÊNITAS E ADQUIRIDAS E NA AVALIAÇÃO E CONTROLE DE HIDROCEFALIA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0205020186', 'CONSISTE NO EXAME DIAGNÓSTICO REALIZADO PELA VAGINA, COM A BEXIGA VAZIA, ONDE O TRANSDUTOR (APARELHO INTRODUZIDO SUAVEMENTE NA VAGINA) TEM UM CALIBRE FINO, ADEQUADO PARA O EXAME, E É PROTEGIDO POR PRESERVATIVO E UM GEL LUBRIFICANTE. CAPTA IMAGENS DE TODO O APARELHO REPRODUTOR E FAZ AVALIAÇÃO DOS ÓRGÃOS GENITAIS INTERNOS (ÚTERO E OVÁRIOS) QUANTO A SUA NORMALIDADE, IDENTIFICANDO EVENTUAIS PATOLOGIAS COMO MIOMAS E NEOPLASIAS OU PARA DETECTAR UMA GRAVIDEZ. PODE TAMBÉM SER REALIZADO PARA CONTROLE DE OVULAÇÃO EM PACIENTES QUE DESEJAM ENGRAVIDAR OU QUE ESTEJAM FAZENDO TRATAMENTO DE INFERTILIDADE. NÃO PODE SER REALIZADO EM MULHERES VIRGENS.', '202507', 1);
