INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0208010068', 'CONSISTE NUM MÉTODO DIAGNÓSTICO POR IMAGEM, ONDE NA TELA DO COMPUTADOR SÃO GERADAS FOTOS OU FILMES DA DISTRIBUIÇÃO DE UM RÁDIO FÁRMACO INJETADO NO PACIENTE QUE PODEM SER ANALISADAS DA FORMA VISUAL OU QUANTITATIVA ATRAVÉS DE CÁLCULOS DA CONCENTRAÇÃO E VELOCIDADE DE MOVIMENTO DESSE RADIO FÁRMACO. SÃO FORMADAS PRIMARIAMENTE IMAGENS NAS QUAIS SE VÊ A FUNÇÃO DOS ÓRGÃOS EM CONTRASTE COM A RADIOLOGIA GERAL EM QUE SÃO FORMADAS IMAGENS ANATÔMICAS EM QUE SE VÊ A FORMA DOS ÓRGÃOS. O RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA. A RADIAÇÃO GAMA É UMA ONDA ELETROMAGNÉTICA. É UM MÉTODO INDOLOR, NÃO INVASIVO (O RADIOTRAÇADOR PODE SER ADMINISTRADO POR VIA VENOSA, ORAL, INALATÓRIA OU SUBCUTÂNEA), NÃO HÁ REAÇÃO ALÉRGICA, MENOR EXPOSIÇÃO À RADIAÇÃO RELACIONADA A OUTRAS TÉCNICAS DE IMAGEM. A DISPONIBILIDADE DE CERTOS RADIOTRAÇADORES NÃO É IMEDIATA, NECESSITANDO, EM ALGUNS CASOS, ESPERA DE 1 A 2 SEMANAS. POR SE TRATAR DE IMAGENS FUNCIONAIS, ALGUNS EXAMES PRECISAM DE PREPARO PRÉVIO PROLONGADO (1 A 90 DIAS) COM RESTRIÇÃO DE CERTOS TIPOS DE ALIMENTOS E MEDICAMENTOS. ALGUNS PROCESSOS FISIOLÓGICOS A SEREM ESTUDADOS NÃO PODEM SER ACELERADOS E A AQUISIÇÃO DAS IMAGENS PODEM LEVAR ATÉ 60 MINUTOS. NESTE PROCEDIMENTO É ESTUDADA A PRESENÇA DE SHUNT EXTRA CARDÍACO E SEU GRAU DE GRAVIDADE.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0208010076', 'CONSISTE NUM MÉTODO DIAGNÓSTICO POR IMAGEM, ONDE NA TELA DO COMPUTADOR SÃO GERADAS FOTOS OU FILMES DA DISTRIBUIÇÃO DE UM RÁDIO FÁRMACO INJETADO NO PACIENTE QUE PODEM SER ANALISADAS DA FORMA VISUAL OU QUANTITATIVA ATRAVÉS DE CÁLCULOS DA CONCENTRAÇÃO E VELOCIDADE DE MOVIMENTO DESSE RADIO FÁRMACO. SÃO FORMADAS PRIMARIAMENTE IMAGENS NAS QUAIS SE VÊ A FUNÇÃO DOS ÓRGÃOS EM CONTRASTE COM A RADIOLOGIA GERAL EM QUE SÃO FORMADAS IMAGENS ANATÔMICAS EM QUE SE VÊ A FORMA DOS ÓRGÃOS. O RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA. A RADIAÇÃO GAMA É UMA ONDA ELETROMAGNÉTICA. É UM MÉTODO INDOLOR, NÃO INVASIVO (O RADIOTRAÇADOR PODE SER ADMINISTRADO POR VIA VENOSA, ORAL, INALATÓRIA OU SUBCUTÂNEA), NÃO HÁ REAÇÃO ALÉRGICA, MENOR EXPOSIÇÃO À RADIAÇÃO RELACIONADA A OUTRAS TÉCNICAS DE IMAGEM. A DISPONIBILIDADE DE CERTOS RADIOTRAÇADORES NÃO É IMEDIATA, NECESSITANDO, EM ALGUNS CASOS, ESPERA DE 1 A 2 SEMANAS. POR SE TRATAR DE IMAGENS FUNCIONAIS, ALGUNS EXAMES PRECISAM DE PREPARO PRÉVIO PROLONGADO (1 A 90 DIAS) COM RESTRIÇÃO DE CERTOS TIPOS DE ALIMENTOS E MEDICAMENTOS. ALGUNS PROCESSOS FISIOLÓGICOS A SEREM ESTUDADOS NÃO PODEM SER ACELERADOS E A AQUISIÇÃO DAS IMAGENS PODEM LEVAR ATÉ 60 MINUTOS. O DIAGNÓSTICO DE DOENÇA ARTERIAL CORONARIANA SUSPEITA OU CONHECIDA É A INDICAÇÃO MAIS COMUM, PODENDO SER REALIZADO EM ESFORÇO (OU COM ESTRESSE FARMACOLÓGICO) E EM REPOUSO. A CINTILOGRAFIA CARDÍACA PERMITE AVALIAR: PRESENÇA E GRAVIDADE DA LESÃO ISQUÊMICA, LOCALIZAÇÃO DO COMPROMETIMENTO CORONARIANO E EXTENSÃO: AVALIA O NÚMERO DE TERRITÓRIOS VASCULARES COMPROMETIDOS), AVALIAÇÃO DA VIABILIDADE MIOCÁRDICA, ISQUEMIA VERSUS FIBROSE (FREQUENTEMENTE SECUNDÁRIA A INFARTO), ESTENOSES CORONARIANAS DE ALTO GRAU PODEM, NA AUSÊNCIA DE INFARTO, CAUSAR HIPOPERFUSÃO MIOCÁRDICA REGIONAL EM REPOUSO E QUE MELHORA NA REDISTRIBUIÇÃO COM TÁLIO-201. ESTIMAR A MELHORA NA FUNÇÃO VENTRICULAR ESQUERDA APÓS A REVASCULARIZAÇÃO MIOCÁRDICA, AVALIAÇÃO DO PROGNÓSTICO, PORÉM NÃO PODEM DIFERENCIAR INFARTO RECENTE OU ANTIGO, MONITORIZAÇÃO APÓS TRATAMENTO REVASCULARIZAÇÃO CORONARIANA. TERAPIA MEDICAMENTOSA PARA INSUFICIÊNCIA CARDÍACA CONGESTIVA OU ANGINA, AVALIAÇÃO DA DOR TORÁCICA AGUDA, DIFERENCIAR ISQUEMIA E MIOCARDIOPATIA IDIOPÁTICA EM PACIENTE COM INSUFICIÊNCIA CARDÍACA CONGESTIVA. O EXAME É REALIZADO EM DUAS ETAPAS: REPOUSO E ESTRESSE. EM CADA ETAPA, HAVERÁ INJEÇÃO NA VEIA DE UM RADIOTRAÇADOR QUE PERMITIRÁ VERIFICAR COMO O SANGUE ESTÁ CHEGANDO NO MIOCÁRDIO. NO REPOUSO, A INJEÇÃO SERÁ FEITA COM A PESSOA SENTADA. NO ESTRESSE, SERÁ REALIZADA ENQUANTO A PESSOA ESTÁ FAZENDO O TESTE ERGOMÉTRICO OU O ESTRESSE INDUZIDO POR UMA MEDICAÇÃO QUE TAMBÉM É INJETADA NA VEIA. SÃO REALIZADAS IMAGENS DO CORAÇÃO NO APARELHO CHAMADO GAMA-CÂMARA CERCA DE 60 A 90 MINUTOS APÓS. O APARELHO GIRA EM TORNO DO TÓRAX, COLETANDO AS IMAGENS DURANTE CERCA DE 5 MINUTOS.', '202507', 1);
