INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0208020020', 'CONSISTE NUM MÉTODO DIAGNÓSTICO POR IMAGEM, ONDE NA TELA DO COMPUTADOR SÃO GERADAS FOTOS OU FILMES DA DISTRIBUIÇÃO DE UM RÁDIO FÁRMACO INJETADO NO PACIENTE QUE PODEM SER ANALISADAS DA FORMA VISUAL OU QUANTITATIVA ATRAVÉS DE CÁLCULOS DA CONCENTRAÇÃO E VELOCIDADE DE MOVIMENTO DESSE RADIO FÁRMACO. SÃO FORMADAS PRIMARIAMENTE IMAGENS NAS QUAIS SE VÊ A FUNÇÃO DOS ÓRGÃOS EM CONTRASTE COM A RADIOLOGIA GERAL EM QUE SÃO FORMADAS IMAGENS ANATÔMICAS EM QUE SE VÊ A FORMA DOS ÓRGÃOS. O RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA. A RADIAÇÃO GAMA É UMA ONDA ELETROMAGNÉTICA. É UM MÉTODO INDOLOR, NÃO INVASIVO (O RADIOTRAÇADOR PODE SER ADMINISTRADO POR VIA VENOSA, ORAL, INALATÓRIA OU SUBCUTÂNEA), NÃO HÁ REAÇÃO ALÉRGICA, MENOR EXPOSIÇÃO À RADIAÇÃO RELACIONADA A OUTRAS TÉCNICAS DE IMAGEM. A DISPONIBILIDADE DE CERTOS RADIOTRAÇADORES NÃO É IMEDIATA, NECESSITANDO, EM ALGUNS CASOS, ESPERA DE 1 A 2 SEMANAS. POR SE TRATAR DE IMAGENS FUNCIONAIS, ALGUNS EXAMES PRECISAM DE PREPARO PRÉVIO PROLONGADO (1 A 90 DIAS) COM RESTRIÇÃO DE CERTOS TIPOS DE ALIMENTOS E MEDICAMENTOS. ALGUNS PROCESSOS FISIOLÓGICOS A SEREM ESTUDADOS NÃO PODEM SER ACELERADOS E A AQUISIÇÃO DAS IMAGENS PODEM LEVAR ATÉ 60 MINUTOS. ANTES DA COLECISTECTOMIA, A CINTILOGRAFIA DE VIAS BILIARES PODE SER UTILIZADA NA CONFIRMAÇÃO DA COLECISTITE AGUDA COM SENSIBILIDADE E ESPECIFICIDADE ELEVADAS (>95%). O PADRÃO CINTILOGRÁFICO DESTA PATOLOGIA É A NÃO VISUALIZAÇÃO DA VESÍCULA BILIAR ATÉ 4 HORAS APÓS A INJEÇÃO DO TRAÇADOR, DEVIDO A OBSTRUÇÃO DO DUCTO CÍSTICO. COM A ADMINISTRAÇÃO DE MORFINA O TEMPO TOTAL DE ESTUDO PODE SER REDUZIDO PARA UMA HORA E MEIA. APÓS A COLECISTECTOMIA, A CINTILOGRAFIA DE VIAS BILIARES PODE SER UTILIZADA NA DETECÇÃO DE FÍSTULAS BILIARES (DETERMINANDO SE COLEÇÕES ABDOMINAIS POSSUEM ORIGEM BILIAR), NA AVALIAÇÃO DA PERVIEDADE DAS ANASTOMOSES BILIODIGESTIVAS, OBSTRUÇÃO (POR CÁLCULO OU ESTENOSE) DO DUCTO HEPÁTICO COMUM OU DISFUNÇÃO DO ESFÍNCTER DE ODDI. ESTE EXAME AUXILIA NA INVESTIGAÇÃO DE LESÕES FOCAIS HEPÁTICAS, NA PESQUISA DE COLECISTITE AGUDA, NA ATRESIA DE VIAS BILIARES, NAS DISFUNÇÕES DA VESÍCULA BILIAR, NO REFLUXO ÊNTERO-GÁSTRICO, PARA IDENTIFICAR CISTOS DE COLÉDOCO, DISFUNÇÕES HEPÁTICAS E BILIARES APÓS TRANSPLANTE HEPÁTICO. EXAME UTILIZADO PARA DIAGNÓSTICO DE COLECISTITE AGUDA, AVALIAÇÃO DA FUNÇÃO DA VESÍCULA BILIAR, CARACTERIZAÇÃO DE LESÕES NO FÍGADO E AVALIAÇÃO DA FUNÇÃO HEPATOCÍTICA.', '202507', 1);
