INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0208040056', 'CONSISTE NUM MÉTODO DIAGNÓSTICO POR IMAGEM, ONDE NA TELA DO COMPUTADOR SÃO GERADAS FOTOS OU FILMES DA DISTRIBUIÇÃO DE UM RÁDIO FÁRMACO INJETADO NO PACIENTE QUE PODEM SER ANALISADAS DA FORMA VISUAL OU QUANTITATIVA ATRAVÉS DE CÁLCULOS DA CONCENTRAÇÃO E VELOCIDADE DE MOVIMENTO DESSE RADIO FÁRMACO. SÃO FORMADAS PRIMARIAMENTE IMAGENS NAS QUAIS SE VÊ A FUNÇÃO DOS ÓRGÃOS EM CONTRASTE COM A RADIOLOGIA GERAL EM QUE SÃO FORMADAS IMAGENS ANATÔMICAS EM QUE SE VÊ A FORMA DOS ÓRGÃOS. O RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA. A RADIAÇÃO GAMA É UMA ONDA ELETROMAGNÉTICA. É UM MÉTODO INDOLOR, NÃO INVASIVO (O RADIOTRAÇADOR PODE SER ADMINISTRADO POR VIA VENOSA, ORAL, INALATÓRIA OU SUBCUTÂNEA), NÃO HÁ REAÇÃO ALÉRGICA, MENOR EXPOSIÇÃO À RADIAÇÃO RELACIONADA A OUTRAS TÉCNICAS DE IMAGEM. A DISPONIBILIDADE DE CERTOS RADIOTRAÇADORES NÃO É IMEDIATA, NECESSITANDO, EM ALGUNS CASOS, ESPERA DE 1 A 2 SEMANAS. POR SE TRATAR DE IMAGENS FUNCIONAIS, ALGUNS EXAMES PRECISAM DE PREPARO PRÉVIO PROLONGADO (1 A 90 DIAS) COM RESTRIÇÃO DE CERTOS TIPOS DE ALIMENTOS E MEDICAMENTOS. ALGUNS PROCESSOS FISIOLÓGICOS A SEREM ESTUDADOS NÃO PODEM SER ACELERADOS E A AQUISIÇÃO DAS IMAGENS PODEM LEVAR ATÉ 60 MINUTOS. EXISTEM DOIS TIPOS DE EXAMES, A CINTILOGRAFIA RENAL DINÂMICA E CINTILOGRAFIA RENAL ESTÁTICA. EXISTEM ALGUMAS DIFERENÇAS ENTRE OS TIPOS DE EXAME COM A UTILIZAÇÃO DO RADIOFÁRMACO. A CINTILOGRAFIA RENAL ESTÁTICA TEM COMO OBJETIVO AVALIAR O TAMANHO, A FORMA, A LOCALIZAÇÃO E A FUNÇÃO DOS RINS. TAMBÉM PODEM SER OBSERVADAS CICATRIZES CAUSADAS POR INFECÇÕES E MÁ-FORMAÇÃO DOS RINS. O RADIOFÁRMACO É ABSORVIDO PELOS RINS E O APARELHO REALIZA A LEITURA DA RADIAÇÃO EMITIDA NA REGIÃO. DEPOIS DE ESTAR PRESENTE NA CORRENTE SANGUÍNEA DO PACIENTE, O RADIOFÁRMACO É FILTRADO, REABSORVIDO E ELIMINADO PELOS RINS. NO CÓRTEX RENAL FICA CONCENTRADA A MAIOR PARTE DO RADIOFÁRMACO. NA IMAGEM CINTILOGRÁFICA É AVALIADO SE O RIM ESTÁ FILTRANDO, REABSORVENDO E ELIMINADO NORMALMENTE. CASO HAJA POUCA ATIVIDADE NAS IMAGENS, É POSSÍVEL IDENTIFICAR AS ZONAS QUE NÃO ESTÃO EM PLENA FUNÇÃO. COMO O DMSA DEMORA PARA SER ELIMINADO, AS IMAGENS DA CINTILOGRAFIA RENAL ESTÁTICA POSSUEM BOAS RESOLUÇÕES, AS IMAGENS SÃO OBTIDAS DE 3 A 6 HORAS APÓS A ADMINISTRAÇÃO DO CONTRASTE. A PRINCIPAL INDICAÇÃO PARA O EXAME DE CINTILOGRAFIA RENAL ESTÁTICA É O DIAGNÓSTICO DA PIELONEFRITE AGUDA E DAS POSSÍVEIS COMPLICAÇÕES E SEQUELAS DA DOENÇA. A IMAGEM DO EXAME TEM ALTA SENSIBILIDADE PARA UM DIAGNÓSTICO PRECOCE, ALÉM DE LOCALIZAR E AVALIAR A EXTENSÃO DO PROCESSO INFLAMATÓRIO. NA CINTILOGRAFIA É POSSÍVEL VISUALIZAR A CICATRIZ RENAL COMO ÁREA DE BAIXA CAPTAÇÃO DE RADIOFÁRMACO COM PERDA DO CONTORNO RENAL E DIMINUIÇÃO DO VOLUME. OUTRAS INDICAÇÕES DO EXAME SÃO: AVALIAÇÃO DA FUNÇÃO TUBULAR DOS RINS; AVALIAÇÃO DA ANATOMIA CORTICAL; DIAGNÓSTICO DE PIELONEFRITE AGUDA; AVALIAÇÃO E SEGUIMENTO DE CASOS DE INFECÇÃO URINÁRIA E/OU PIELONEFRITES DE REPETIÇÃO (CICATRIZES CORTICAIS); DIAGNÓSTICO DE ANOMALIAS RENAIS (EX.: RIM EM FERRADURA, ECTOPIA RENAL CRUZADA, RIM ÚNICO, HIPOPLASIA RENAL, RIM PÉLVICO, CISTOS); DIAGNÓSTICO DIFERENCIAL DE PSEUDOTUMORES RENAIS (EX.: HIPERTROFIA DA COLUNA DE BERTIN E LOBULAÇÃO FETAL X TUMOR MALIGNO). É UM TRAÇADO GRÁFICO DE RADIOATIVIDADE MEDIDA EXTERNAMENTE SOBRE OS RINS, DURANTE UM PERÍODO DE TEMPO, APÓS A INJEÇÃO INTRAVENOSA DE UM RADIONUCLÍDEO QUE É RETIRADO E EXCRETADO PELOS RINS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0208040064', 'CONSISTE NUM MÉTODO DIAGNÓSTICO POR IMAGEM, ONDE NA TELA DO COMPUTADOR SÃO GERADAS FOTOS OU FILMES DA DISTRIBUIÇÃO DE UM RÁDIO FÁRMACO INJETADO NO PACIENTE QUE PODEM SER ANALISADAS DA FORMA VISUAL OU QUANTITATIVA ATRAVÉS DE CÁLCULOS DA CONCENTRAÇÃO E VELOCIDADE DE MOVIMENTO DESSE RADIO FÁRMACO. SÃO FORMADAS PRIMARIAMENTE IMAGENS NAS QUAIS SE VÊ A FUNÇÃO DOS ÓRGÃOS EM CONTRASTE COM A RADIOLOGIA GERAL EM QUE SÃO FORMADAS IMAGENS ANATÔMICAS EM QUE SE VÊ A FORMA DOS ÓRGÃOS. O RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA. A RADIAÇÃO GAMA É UMA ONDA ELETROMAGNÉTICA. É UM MÉTODO INDOLOR, NÃO INVASIVO (O RADIOTRAÇADOR PODE SER ADMINISTRADO POR VIA VENOSA, ORAL, INALATÓRIA OU SUBCUTÂNEA), NÃO HÁ REAÇÃO ALÉRGICA, MENOR EXPOSIÇÃO À RADIAÇÃO RELACIONADA A OUTRAS TÉCNICAS DE IMAGEM. A DISPONIBILIDADE DE CERTOS RADIOTRAÇADORES NÃO É IMEDIATA, NECESSITANDO, EM ALGUNS CASOS, ESPERA DE 1 A 2 SEMANAS. POR SE TRATAR DE IMAGENS FUNCIONAIS, ALGUNS EXAMES PRECISAM DE PREPARO PRÉVIO PROLONGADO (1 A 90 DIAS) COM RESTRIÇÃO DE CERTOS TIPOS DE ALIMENTOS E MEDICAMENTOS. ALGUNS PROCESSOS FISIOLÓGICOS A SEREM ESTUDADOS NÃO PODEM SER ACELERADOS E A AQUISIÇÃO DAS IMAGENS PODEM LEVAR ATÉ 60 MINUTOS.  É O EXAME DE IMAGEM DA MEDICINA NUCLEAR QUE ESTUDA A FUNÇÃO RENAL, A FORMAÇÃO E A EXCREÇÃO DA URINA PARA A BEXIGA. O PREPARO PARA O PACIENTE ANTES DO EXAME É ESTAR BEM HIDRATADO. A CISTOCINTILOGRAFIA AJUDA A DIAGNOSTICAR REFLUXOS VESICOURETERAIS, ANOMALIA RELACIONADA A INFECÇÕES URINÁRIAS. O PROBLEMA OCORRE QUANDO A URINA VINDA DOS RINS NÃO RETORNA PARA OS URETERES, POR CONTA DO NÃO FUNCIONAMENTO DO MECANISMO VALVULAR, PROPORCIONANDO O REFLUXO URINÁRIO E AUMENTANDO OS RISCOS DE BACTÉRIAS. NA CISTOCINTILOGRAFIA DIRETA É UTILIZADA UMA SONDA VESICAL E O PACIENTE DEVERÁ ESVAZIAR A BEXIGA ANTES DO EXAME. AS IMAGENS SÃO REALIZADAS DURANTE O ENCHIMENTO E ESVAZIAMENTO DA BEXIGA. PARA REALIZAR O PROCEDIMENTO, A SONDA VESICAL É COLOCADA E, LOGO APÓS O PACIENTE É LEVADO ATÉ A CÂMARA DE CINTILAÇÃO E O PACIENTE É POSICIONADO SENTADO E É INJETADO O RADIOFÁRMACO JUNTO COM SORO FISIOLÓGICO ATRAVÉS DA SONDA. A CAPTAÇÃO DAS IMAGENS DURA APROXIMADAMENTE 60 MINUTOS, OU ATÉ O PACIENTE SENTIR VONTADE DE URINAR. AO ESVAZIAR A BEXIGA, AS IMAGENS SÃO CAPTADAS.', '202507', 1);
