INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0208050019', 'CONSISTE NUM MÉTODO DIAGNÓSTICO POR IMAGEM, ONDE NA TELA DO COMPUTADOR SÃO GERADAS FOTOS OU FILMES DA DISTRIBUIÇÃO DE UM RÁDIO FÁRMACO INJETADO NO PACIENTE QUE PODEM SER ANALISADAS DA FORMA VISUAL OU QUANTITATIVA ATRAVÉS DE CÁLCULOS DA CONCENTRAÇÃO E VELOCIDADE DE MOVIMENTO DESSE RADIO FÁRMACO. SÃO FORMADAS PRIMARIAMENTE IMAGENS NAS QUAIS SE VÊ A FUNÇÃO DOS ÓRGÃOS EM CONTRASTE COM A RADIOLOGIA GERAL EM QUE SÃO FORMADAS IMAGENS ANATÔMICAS EM QUE SE VÊ A FORMA DOS ÓRGÃOS. O RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA. A RADIAÇÃO GAMA É UMA ONDA ELETROMAGNÉTICA. É UM MÉTODO INDOLOR, NÃO INVASIVO (O RADIOTRAÇADOR PODE SER ADMINISTRADO POR VIA VENOSA, ORAL, INALATÓRIA OU SUBCUTÂNEA), NÃO HÁ REAÇÃO ALÉRGICA, MENOR EXPOSIÇÃO À RADIAÇÃO RELACIONADA A OUTRAS TÉCNICAS DE IMAGEM. A DISPONIBILIDADE DE CERTOS RADIOTRAÇADORES NÃO É IMEDIATA, NECESSITANDO, EM ALGUNS CASOS, ESPERA DE 1 A 2 SEMANAS. POR SE TRATAR DE IMAGENS FUNCIONAIS, ALGUNS EXAMES PRECISAM DE PREPARO PRÉVIO PROLONGADO (1 A 90 DIAS) COM RESTRIÇÃO DE CERTOS TIPOS DE ALIMENTOS E MEDICAMENTOS. ALGUNS PROCESSOS FISIOLÓGICOS A SEREM ESTUDADOS NÃO PODEM SER ACELERADOS E A AQUISIÇÃO DAS IMAGENS PODEM LEVAR ATÉ 60 MINUTOS. A CINTILOGRAFIA ÓSSEA É UM EXAME DE IMAGEM UTILIZADO, NA MAIORIA DAS VEZES, PARA IDENTIFICAR SINAIS DE CÂNCER OU METÁSTASES PARA OS OSSOS, ALÉM DE IDENTIFICAR PONTOS DE INFLAMAÇÃO CAUSADOS POR INFECÇÕES, ARTRITES, FRATURAS, ALTERAÇÕES NA CIRCULAÇÃO SANGUÍNEA DO OSSO, AVALIAÇÃO DE PRÓTESES ÓSSEAS OU PARA INVESTIGAR CAUSAS DE DOR NOS OSSOS.  É INJETADO NA VEIA UM RADIOFÁRMACO, COMO GÁLIO, QUE SÃO SUBSTÂNCIAS RADIOATIVAS. ESTAS SUBSTÂNCIAS SÃO ATRAÍDAS PELO TECIDO ÓSSEO COM A DOENÇA APÓS CERCA DE 2 HORAS, O QUE PODE SER REGISTRADO UTILIZANDO-SE UMA CÂMARA ESPECIAL, QUE DETECTA A RADIOATIVIDADE E CRIA UMA IMAGEM DO ESQUELETO. OS LOCAIS ONDE O RADIOFÁRMACO SE CONCENTROU MAIS SÃO DESTACADOS, O QUE SIGNIFICA INTENSA REAÇÃO METABÓLICA NA REGIÃO, COMO MOSTRA A IMAGEM. A CINTILOGRAFIA ÓSSEA PODE SER REALIZADA PARA ALGUMA REGIÃO ESPECÍFICA OU PARA O CORPO INTEIRO E, NORMALMENTE, O EXAME DURA ENTRE 30-40 MINUTOS. O PACIENTE NÃO NECESSITA DE FAZER JEJUM, NEM DE TER NENHUM CUIDADO ESPECIAL, OU SUSPENDER A MEDICAÇÃO. NO ENTANTO, NAS 24 HORAS SEGUINTES AO EXAME, O PACIENTE NÃO DEVE ENTRAR COM CONTATO COM GRÁVIDAS OU CRIANÇAS PEQUENAS, POIS PODEM SER SENSÍVEIS AO RADIOFÁRMACO QUE É ELIMINADO DURANTE ESSE PERÍODO. A CINTILOGRAFIA ÓSSEA PODE SER INDICADA NAS SEGUINTES SITUAÇÕES: PESQUISA DE METÁSTASES ÓSSEAS CAUSADAS POR VARIADOS TIPOS CÂNCER, COMO DE MAMA, PRÓSTATA OU PULMÃO, POR EXEMPLO, PARA IDENTIFICAR ÁREAS DE ALTERAÇÃO DO METABOLISMO DOS OSSOS, PARA IDENTIFICAR ALTERAÇÕES CAUSADAS POR OSTEOMIELITE, ARTRITES, TUMORES ÓSSEOS PRIMÁRIOS, FRATURAS, OSTEONECROSE, DISTROFIA SIMPÁTICA REFLEXA, INFARTO ÓSSEO, VIABILIDADE DO ENXERTO ÓSSEO E AVALIAÇÃO DE PRÓTESES ÓSSEAS. TAMBÉM É UTILIZADA PARA INVESTIGAR CAUSAS DE DOR ÓSSEA EM QUE NÃO FORAM IDENTIFICADAS AS CAUSAS COM OUTROS EXAMES.', '202507', 1);
