INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0208080040', 'CONSISTE NUM MÉTODO DIAGNÓSTICO POR IMAGEM, ONDE NA TELA NO COMPUTADOR SÃO GERADAS FOTOS OU FILMES DA DISTRIBUIÇÃO DE UM RÁDIO FÁRMACO INJETADO NO PACIENTE QUE PODEM SER ANALISADAS DA FORMA VISUAL OU QUANTITATIVA ATRAVÉS DE CÁLCULOS DA CONCENTRAÇÃO E VELOCIDADE DE MOVIMENTO DESSE RADIO FÁRMACO. SÃO FORMADAS PRIMARIAMENTE IMAGENS NAS QUAIS SE VÊ A FUNÇÃO DOS ÓRGÃOS EM CONTRASTE COM A RADIOLOGIA GERAL EM QUE SÃO FORMADAS IMAGENS ANATÔMICAS EM QUE SE VÊ A FORMA DOS ÓRGÃOS. O RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA.  RÁDIO FÁRMACO É A UNIÃO DE UM RADIOISÓTOPO ANÁLOGO DE UMA MOLÉCULA FISIOLÓGICA ESCOLHIDO DE ACORDO COM O ÓRGÃO E FUNÇÃO A SER ESTUDADA. A RADIAÇÃO GAMA É UMA ONDA ELETROMAGNÉTICA SEMELHANTE À LUZ VISÍVEL, E SUA CINTILAÇÃO É APENAS VISTA ATRAVÉS DA GAMA CÂMARA PERMITINDO A VISUALIZAÇÃO DE IMAGENS DOS ÓRGÃOS INTERNOS.   IMAGEM. A DISPONIBILIDADE DE CERTOS RADIOTRAÇADORES NÃO É IMEDIATA, NECESSITANDO, EM ALGUNS CASOS, ESPERA DE 1 A 2 SEMANAS. POR SE TRATAR DE IMAGENS FUNCIONAIS, ALGUNS EXAMES PRECISAM DE PREPARO PRÉVIO PROLONGADO (1 A 90 DIAS) COM RESTRIÇÃO DE CERTOS TIPOS DE ALIMENTOS E MEDICAMENTOS. ALGUNS PROCESSOS FISIOLÓGICOS A SEREM ESTUDADOS NÃO PODEM SER ACELERADOS E A AQUISIÇÃO DAS IMAGENS PODEM LEVAR ATÉ 60 MINUTOS. A LINFOCINTILOGRAFIA É, ATUALMENTE, O EXAME DE ESCOLHA PARA AVALIAR O SISTEMA LINFÁTICO, POIS AVALIA A FUNÇÃO E A ANATOMIA DO SISTEMA LINFÁTICO, SENDO UM MÉTODO POUCO INVASIVO, DE FÁCIL REALIZAÇÃO E PODER SER REPETIDO SEM CAUSAR DANO AO VASO LINFÁTICO.||ESSE EXAME NÃO UTILIZA CONTRASTES E NÃO ENVOLVE A DISSECÇÃO DE VASOS LINFÁTICOS, PODE SER UTILIZADO COM SEGURANÇA EM CRIANÇAS E, PRINCIPALMENTE, PERMITE O ESTUDO TANTO DA ANATOMIA QUANTO DA FISIOLOGIA DA CIRCULAÇÃO LINFÁTICA. É REALIZADA PELA INJEÇÃO INTRADÉRMICA DE RADIOFÁRMACO NA EXTREMIDADE DOS MEMBROS E AQUISIÇÃO DE IMAGENS ATRAVÉS DE UMA GAMA-CÂMARA. VÁRIOS RADIOISÓTOPOS SÃO EMPREGADOS PARA A REALIZAÇÃO DO ESTUDO LINFOCINTIGRÁFICO, PORÉM O MAIS UTILIZADO É O TECNÉCIO-99 METAESTÁVEL (TC-99M).||O COMPORTAMENTO BIOCINÉTICO DAS PARTÍCULAS INJETADAS NO INTERSTÍCIO DEPENDE PRINCIPALMENTE DO SEU DIÂMETRO. AS PARTÍCULAS QUE APRESENTAM DIÂMETRO INFERIOR A 10 NM SÃO ABSORVIDAS, PREFERENCIALMENTE, PELO SISTEMA CAPILAR SANGÜÍNEO, ENQUANTO QUE AQUELAS CUJO DIÂMETRO SITUA-SE ENTRE 10 E 50 NM SÃO RAPIDAMENTE TRANSPORTADAS ATRAVÉS DOS VASOS CAPILARES LINFÁTICOS. A LINFOCINTILOGRAFIA PODE SER INTERPRETADA DE TRÊS MANEIRAS: QUANTITATIVA, QUE AVALIA O TRANSPORTE DO RADIOFÁRMACO EM RELAÇÃO AO TEMPO; QUALITATIVA, QUE ANALISA VISUALMENTE AS IMAGENS; E SEMIQUANTITATIVA, QUE ASSOCIA DADOS DA DINÂMICA DO TRANSPORTE DO RADIOFÁRMACO COM O TEMPO DE APARECIMENTO DA RADIOATIVIDADE.||', '202507', 1);
