INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0209010045', 'CONSISTE NUMA INTERVENÇÃO CIRÚRGICA MINIMAMENTE INVASIVA, MUITO UTILIZADA EM CIRURGIAS GINECOLÓGICAS E UROLÓGICAS, CONSAGRADA PARA A RETIRADA DA VESÍCULA BILIAR. A EVOLUÇÃO DA TECNOLOGIA PERMITE ACESSAR PRATICAMENTE TODOS OS ÓRGÃOS DA CAVIDADE ABDOMINAL E SEU CONTEÚDO COM APARELHOS  É REALIZADA SOB ANESTESIA E CONSISTE NUMA PEQUENA INCISÃO NA REGIÃO A SER EXAMINADA OU TRATADA, POR ONDE É INTRODUZIDO O LAPAROSCÓPIO, QUE É UM FINO TUBO DE FIBRAS ÓTICAS, ATRAVÉS DO QUAL PODE VISUALIZAR OS ÓRGÃOS INTERNOS E FAZER INTERVENÇÕES DIAGNÓSTICAS OU TERAPÊUTICAS. OUTRAS PEQUENAS INCISÕES PODEM SER NECESSÁRIAS PARA INTRODUZIR OS INSTRUMENTOS CIRÚRGICOS. CERTA QUANTIDADE DE GÁS (DIÓXIDO DE CARBONO) É INTRODUZIDA DENTRO DA CAVIDADE ABDOMINAL A FIM DE EXPANDI-LA E CRIAR UM CAMPO DE TRABALHO PARA SE REALIZAR A CIRURGIA. ESTA TÉCNICA TEM A VANTAGEM DE MENOR TRAUMA CIRÚRGICO, MENOS SANGRAMENTO INTRAOPERATÓRIO, MENOR DOR PÓS-OPERATÓRIA, RECUPERAÇÃO PÓS-CIRÚRGICA MAIS RÁPIDA E RETORNO MAIS CEDO ÀS ATIVIDADES HABITUAIS E AO TRABALHO, ALÉM DE MENORES CICATRIZES. ELA REDUZ A TAXA DE INFECÇÕES E A OCORRÊNCIA DE ADERÊNCIAS PÓS-OPERATÓRIAS. PRATICAMENTE TODAS AS CIRURGIAS GINECOLÓGICAS (CISTOS DE OVÁRIO, DILATAÇÃO DAS TROMPAS, TORÇÃO DE OVÁRIO, GRAVIDEZ ECTÓPICA, ETC.) E UROLÓGICAS PODEM SER REALIZADAS POR LAPAROSCOPIA, ALÉM DA RETIRADA E OS PROLAPSOS DO ÚTERO, BEM COMO A CISTOCELE OU RETOCELE.', '202507', 1);
