INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0210010070', 'É O ESTUDO ARTERIOGRÁFICO DE UM MEMBRO SUPERIOR OU DE UM MEMBRO INFERIOR.  O CÓDIGO DEVE SER UTILIZADO POR CADA MEMBRO. A ARTERIOGRAFIA CONSISTE NUM MÉTODO DIAGNÓSTICO, MINIMAMENTE EVASIVO, REALIZADO PARA ESTUDO DAS DOENÇAS ARTERIAIS. O EXAME ACESSA O ESPAÇO INTRAVASCULAR DE UMA ARTÉRIA ATRAVÉS DE PUNÇÃO, COM O USO DE CATETERES ESPECIAIS E GUIAS, NAVEGA POR DENTRO DAS ARTÉRIAS PARA OS MAIS DIVERSOS LOCAIS DO CORPO, ORIENTANDO-SE POR IMAGENS EM TEMPO REAL NA TELA DO COMPUTADOR. UMA VEZ ATINGIDO O LOCAL DE INTERESSE, É INJETADO CONTRASTE RADIOLÓGICO E ADQUIRIDAS IMAGENS DIGITAIS. A ARTERIOGRAFIA PODE SER ELETIVA OU DE URGÊNCIA. ELETIVA: INDICADA PRINCIPALMENTE PARA O DIAGNÓSTICO E AVALIAÇÃO DA GRAVIDADE DA DOENÇA VASCULAR CEREBRAL DA AORTA E DE ARTÉRIAS PERIFÉRICAS, INVESTIGA ANEURISMAS E MÁ FORMAÇÃO ARTERIAL. NA EMERGÊNCIA É INDICADA PARA DOENÇAS AGUDAS COMO A DISSECÇÃO AÓRTICA, EMBOLIAS OU TROMBOSES.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0210010088', 'CONSISTE NO EXAME DE IMAGEM TAMBÉM DENOMINADO COMO ANGIOGRAFIA DIGITAL QUE PERMITE AVALIAR A CIRCULAÇÃO SANGUÍNEA, NESTE CASO POR VIA VENOSA. É REALIZADO COM INJEÇÃO DE CONTRASTE NOS VASOS SANGUÍNEOS, MONITORAMENTO COM APARELHO DIGITAL, PRODUÇÃO DE IMAGENS DE ALTA PRECISÃO PARA DIAGNOSTICAR PROBLEMAS VASCULARES, IDENTIFICAR ALTERAÇÕES OU LESÕES NOS VASOS E AUXILIAR NO DIAGNÓSTICO DE DOENÇAS COMO O INFARTO DO MIOCÁRDIO, ACIDENTES VASCULARES CEREBRAIS(AVC), ESTENOSES DA ARTÉRIA RENAL E EMBOLIAS PULMONARES.', '202507', 1);
