INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0210010142', 'É O ESTUDO ANGIOGRÁFICO SELETIVO DE UM SÓ VASO EM QUALQUER SEGMENTO ANATÔMICO. EXCLUÍDO O SISTEMA NERVOSO CENTRAL, CARÓTIDA, VERTEBRAL, SUBCLÁVIA,CIRCULAÇÃO CÉRVICO TORÁCICA AORTA, ARTÉRIAS ILÍACAS E FEMURAIS COMUM E MEMBROS. A ARTERIOGRAFIA CONSISTE NUM MÉTODO DIAGNÓSTICO, MINIMAMENTE EVASIVO, REALIZADO PARA ESTUDO DAS DOENÇAS ARTERIAIS. O EXAME ACESSA O ESPAÇO INTRAVASCULAR DE UMA ARTÉRIA ATRAVÉS DE PUNÇÃO, COM O USO DE CATETERES ESPECIAIS E GUIAS, NAVEGA POR DENTRO DAS ARTÉRIAS PARA OS MAIS DIVERSOS LOCAIS DO CORPO, ORIENTANDO-SE POR IMAGENS EM TEMPO REAL NA TELA DO COMPUTADOR. UMA VEZ ATINGIDO O LOCAL DE INTERESSE, É INJETADO CONTRASTE RADIOLÓGICO E ADQUIRIDAS IMAGENS DIGITAIS. A ARTERIOGRAFIA PODE SER ELETIVA OU DE URGÊNCIA. ELETIVA: INDICADA PRINCIPALMENTE PARA O DIAGNÓSTICO E AVALIAÇÃO DA GRAVIDADE DA DOENÇA VASCULAR CEREBRAL DA AORTA E DE ARTÉRIAS PERIFÉRICAS, INVESTIGA ANEURISMAS E MÁ FORMAÇÃO ARTERIAL. NA EMERGÊNCIA É INDICADA PARA DOENÇAS AGUDAS COMO A DISSECÇÃO AÓRTICA, AS EMBOLIAS OU AS TROMBOSES.', '202507', 1);
