INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0210010177', 'A FLEBOGRAFIA OU VENOGRAFIA CONSISTE NO PROCEDIMENTO NO QUAL IMAGENS DAS VEIAS SÃO REALIZADAS APÓS A INJEÇÃO DE UM CONTRASTE CONTENDO IODO NUMA VEIA, USUALMENTE DO PÉ OU DA MÃO. O CONTRASTE DEVE SER INJETADO CONSTANTEMENTE ATRAVÉS DE UM CATÉTER, O QUE CONFIGURA O PROCEDIMENTO COMO INVASIVO. FOI MUITO USADO PARA DIAGNOSTICAR TROMBOSE VENOSA PROFUNDA, SENDO ATUALMENTE, POIS A ECOGRAFIA E O DOPPLER FACILITARAM O DIAGNÓSTICO PELA PRECISÃO, CONFORTO PARA O DOENTE E MENOR CUSTO. EVENTUALMENTE SER USADO PARA FINS DE INVESTIGAÇÃO OU EM CERTOS CASOS DE MALFORMAÇÕES VENOSAS. PERMITE AVALIAR A ANATOMIA DE UMA VEIA. A FLEBOGRAFIA, POR SER UM PROCEDIMENTO INVASIVO DEVE SER REALIZADA SOMENTE NOS CASOS EM QUE OS EXAMES NÃO INVASIVOS NÃO FORAM CONCLUSIVOS OU QUANDO É NECESSÁRIA A AVALIAÇÃO DE ESTRUTURAS VENOSAS, COMO AS VÁLVULAS. AS FLEBOGRAFIAS SÃO REALIZADAS TAMBÉM PARA GUIAR ALGUNS PROCEDIMENTOS ENDOVASCULARES, BEM COMO PARA AJUDAR NA PROGRAMAÇÃO PRÉ-OPERATÓRIA. PODE SER FEITO O REGISTO GRÁFICO DO PULSO DURANTE O EXAME E PERMITE LOCALIZAR TROMBOS (COÁGULOS) NAS VEIAS OU ARTÉRIAS. PODE SER ADMINISTRADO AO PACIENTE UM SEDATIVO OU ANALGÉSICO ANTES DO EXAME.', '202507', 1);
