INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0210020016', 'CONSISTE NO EXAME DAS VIAS BILIARES, PARA VISUALIZAÇÃO DO TRAJETO DA BILE DESDE O FÍGADO ATÉ O DUODENO. PERMITE DIAGNOSTICAR OBSTRUÇÃO À PASSAGEM DA BILE, PROVOCADA POR TUMOR, CÁLCULO OU CORPO ESTRANHO. TAMBÉM PERMITE VERIFICAR O FUNCIONAMENTO DA AMPOLA DE VATER, LESÕES, ESTREITAMENTO OU DILATAÇÃO DOS DUCTOS BILIARES. ESTE EXAME TAMBÉM PODE SER FEITO POR OUTRAS VIAS ALÉM DA TRANSCUTÂNEA.  (POR RX  COLANGIOGRAFIA VENOSA, VIA ENDOSCÓPICA, TRANS-OPERATÓRIA, COLANGIOGRAFIA TRANSPARIETAL, TRANSHEPÁTICA E POR RESSONÂNCIA MAGNÉTICA).  A COLANGIOGRAFIA TRANS-HEPÁTICA PERCUTÂNEA É FEITA INJETANDO-SE MEIO DE CONTRASTE SOB VISÃO FLUOROSCÓPICA ATRAVÉS DE UMA AGULHA DE PEQUENO CALIBRE INTRODUZIDA NO PARÊNQUIMA DO FÍGADO E TEM VANTAGEM DE PERMITIR  DRENAGEM BILIAR, SE NECESSÁRIA. É UTILIZADO NO DIAGNÓSTICO DIFERENCIAL DE COLESTASE INTRA E EXTRA-HEPÁTICA E PARA ESTUDO DAS CONDIÇÕES DA ÁRVORE BILIAR.', '202507', 1);
