INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211010030', 'CONSISTE NUM PROCEDIMENTO NÃO INVASIVO DE AVALIAÇÃO MECÂNICA DO NÍVEL OU GRAU DE OBSTRUÇÃO DAS VIAS AÉREAS ATRAVÉS DE ONDAS SONORAS SOBREPOSTAS À RESPIRAÇÃO NORMAL, DE FORMA NÃO INVASIVA E COM PEQUENA COOPERAÇÃO DO PACIENTE. NÃO É O MESMO QUE ESPIROMETRIA JÁ TEM SEUS CRITÉRIOS E GRADUAÇÕES BEM DEFINIDOS, MAS NECESSITA DE ESFORÇO VENTILATÓRIO E MANOBRAS NEM SEMPRE DE QUALIDADE TÉCNICA ACESSÍVEL. TEM COMO OBJETIVO AVALIAR AS ALTERAÇÕES DA MECÂNICA RESPIRATÓRIA EM RELAÇÃO À IMPEDÂNCIA DAS VIAS AÉREAS, EM PACIENTES COM DISTÚRBIO VENTILATÓRIO OBSTRUTIVO (DVO). ESTUDA A FUNÇÃO RESPIRATÓRIA POR MEIO DE UM GERADOR EXTERNO DE IMPULSOS COMO FONTE DE FORÇA AO CONTRÁRIO DAS OUTRAS TÉCNICAS QUE USAM OS MÚSCULOS RESPIRATÓRIOS. OS IMPULSOS GERAM OSCILAÇÕES DE FLUXO QUE SE SOBREPÕEM À VENTILAÇÃO ESPONTÂNEA PERMITINDO ANALISAR AS RESPECTIVAS RESPOSTAS DE VARIAÇÃO DE PRESSÃO E DÉBITO A ESTES IMPULSOS. A EXECUÇÃO TÉCNICA É SIMPLES UMA VEZ QUE NÃO É NECESSÁRIA A REALIZAÇÃO DE MANOBRA DO PACIENTE, PERMITINDO SER REALIZADOS EM CRIANÇAS (> 2 ANOS), IDOSOS E ACAMADOS. O TRAÇADO OSCILOMÉTRICO CONSEGUE-SE COM COLABORAÇÃO MÍNIMA DO DOENTE QUE SE TRADUZ NUMA RESPIRAÇÃO ESPONTÂNEA CALMA E ESTÁVEL. O REGISTO INICIA-SE QUANDO O VOLUME CORRENTE ESTÁ ESTABILIZADO E TERMINA, EM REGRA, APÓS 30 SEGUNDOS DE BOM TRAÇADO. A ZONA DE BOM TRAÇADO PODE SER IDENTIFICADA E DEPOIS SELECIONADA, SENDO OS VALORES DOS PARÂMETROS AUTOMATICAMENTE CALCULADOS. O PACIENTE DEVE ESTAR SENTADO, CONECTADO A UM BOCAL PRÓPRIO SEM INTERPOSIÇÃO DA LÍNGUA, COM PINÇA NASAL, A CABEÇA EM POSIÇÃO NEUTRA OU EM LIGEIRA EXTENSÃO E MÃOS NAS BOCHECHAS PARA EVITAR A SUA DISTENSÃO EXAGERADA. TERMINA-SE O EXAME COM O DOENTE A RESPIRAR NO BOCAL.', '202507', 1);
