INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211020010', 'CONSISTE NO PROCEDIMENTO PARA DIAGNOSTICAR OU TRATAR DOENÇAS CARDÍACAS, POR MEIO DA INTRODUÇÃO DE UM CATÉTER, QUE É UM TUBO FLEXÍVEL EXTREMAMENTE FINO E LONGO, NA ARTÉRIA DO BRAÇO OU DA PERNA DO INDIVÍDUO, QUE SERÁ CONDUZIDO ATÉ O CORAÇÃO. TAMBÉM DENOMINADO DE ANGIOGRAFIA CORONÁRIA, CINECORONARIOGRAFIA OU AINDA ESTUDO HEMODINÂMICO. É INDICADO NO DIAGNÓSTICO E TRATAMENTO DO INFARTO OU DA ANGINA, SENDO CAPAZ DE DETECTAR E REMOVER ACÚMULOS DE PLACAS DE GORDURA, COLESTEROL, CÁLCIO E OUTRAS SUBSTÂNCIAS ENCONTRADAS NO SANGUE, MOSTRAR SE AS PLACAS ESTREITARAM OU BLOQUEARAM AS ARTÉRIAS CORONÁRIAS. O ACÚMULO DE PLACAS ESTREITA O INTERIOR DAS ARTÉRIAS E RESTRINGE O FLUXO DE SANGUE AO CORAÇÃO. O CATETERISMO CARDÍACO É MUITO UTILIZADO PARA DIAGNOSTICAR E/OU TRATAR DIVERSAS CONDIÇÕES CARDÍACAS, DENTRE AS QUAIS: AVALIAR AS ARTÉRIAS CORONÁRIAS QUE IRRIGAM A MUSCULATURA DO CORAÇÃO, DESOBSTRUIR ARTÉRIAS E VÁLVULAS DEVIDO AO ACÚMULO DE PLACAS DE GORDURA, VERIFICAR SE EXISTEM LESÕES NAS VÁLVULAS E DO MÚSCULO CARDÍACO, VERIFICAR A EXISTÊNCIA DE ALTERAÇÕES NA ANATOMIA DO CORAÇÃO NÃO CONFIRMADAS POR OUTROS EXAMES, MOSTRAR EM DETALHES MALFORMAÇÃO CONGÊNITA EM RECÉM-NASCIDOS E CRIANÇAS. A RECUPERAÇÃO DO PROCEDIMENTO É RÁPIDA, E NÃO HAVENDO  COMPLICAÇÕES QUE IMPEÇAM, O PACIENTE RECEBE ALTA LOGO APÓS ALGUMAS HORAS, DESDE QUE NÃO HAJA OUTRO PROCEDIMENTO ASSOCIADO. FEITO SOB ANESTESIA LOCAL, É REALIZADA UMA PEQUENA ABERTURA PARA ENTRADA DO CATÉTER NA PELE DA VIRILHA OU DO ANTEBRAÇO NA ALTURA DO PUNHO OU COTOVELO, A SEGUIR É FEITA A INSERÇÃO DO CATÉTER NA ARTÉRIA (GERALMENTE, RADIAL, FEMORAL OU BRAQUEAL) QUE SERÁ CONDUZIDO ATÉ O CORAÇÃO. SÃO LOCALIZADAS AS ENTRADAS DAS ARTÉRIAS CORONÁRIAS DIREITA E ESQUERDA E A SEGUIR A INJEÇÃO DE CONTRASTE À BASE DE IODO QUE PERMITE A VISUALIZAÇÃO DAS IMAGENS DAS ARTÉRIAS E DE POSSÍVEIS PONTOS DE ENTUPIMENTO. TAMBÉM É INJETADO CONTRASTE NO VENTRÍCULO ESQUERDO PARA VISUALIZAÇÃO DO BOMBEAMENTO CARDÍACO. O EXAME NÃO CAUSA DORES, SENDO NORMAL O PACIENTE SENTIR UMA ONDA PASSAGEIRA DE CALOR NO PEITO QUANDO SE INJETA O CONTRASTE. NORMALMENTE, O EXAME NÃO DEMORA MAIS QUE 30 MINUTOS, SENDO GERALMENTE MAIS LONGO EM PACIENTES JÁ SUBMETIDOS À CIRURGIA DE REVASCULARIZAÇÃO DO MIOCÁRDIO OU DOENÇA DA ARTÉRIA CORONARIANA. BLOQUEIOS NAS ARTÉRIAS TAMBÉM PODEM SER VISTOS USANDO ULTRASSONOGRAFIA DURANTE O CATETERISMO CARDÍACO PARA AUXILIAR NA DEFINIÇÃO OU NÃO DA NECESSIDADE DE TRATAMENTO CIRÚRGICO (ANGIOPLASTIA CORONÁRIA, CIRURGIA CARDÍACA OU CORREÇÃO DE CARDIOPATIAS CONGÊNITAS). A JUSTIFICATIVA MAIS COMUM DA SOLICITAÇÃO DO CATETERISMO É PARA AVALIAR DOR NO PEITO, SINTOMA DE DOENÇA DA ARTÉRIA CORONÁRIA MOSTRANDO SE A PLACA ESTÁ ESTREITANDO OU BLOQUEANDO AS ARTÉRIAS CARDÍACAS. É UM EXAME INVASIVO QUE PODE SER REALIZADO DE FORMA ELETIVA, PARA CONFIRMAR A PRESENÇA DE OBSTRUÇÕES DAS ARTÉRIAS CORONÁRIAS OU AVALIAR O FUNCIONAMENTO DAS VALVAS E DO MÚSCULO CARDÍACO OU EM SITUAÇÕES DE EMERGÊNCIA, PARA DETERMINAR A EXATA LOCALIZAÇÃO DA OBSTRUÇÃO QUE ESTÁ CAUSANDO O INFARTO AGUDO DO MIOCÁRDIO E PLANEJAR A MELHOR ESTRATÉGIA DE INTERVENÇÃO. TODO O EXAME ENVOLVE A AQUISIÇÃO E GERAÇÃO DE IMAGENS QUE SÃO POSTERIORMENTE DISPONIBILIZADAS, EM FILME E FOTOS, PARA O PACIENTE. QUANDO DA PUNÇÃO DA ARTÉRIA (SEJA NO BRAÇO OU NA VIRILHA), É INSTALADO UM INTRODUTOR, POR ONDE O CATETER SERÁ INTRODUZIDO. ESTABELECIDO O DIAGNÓSTICO E O GRAU DA OBSTRUÇÃO ARTERIAL, O CARDIOLOGISTA INTERVENCIONISTA PODERÁ DECIDIR POR TRATAMENTO INTERVENCIONISTA IMEDIATO, QUE SERIA A ANGIOPLASTIA CORONÁRIA OU PROGRAMAR O TRATAMENTO SUBSEQUENTE.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211020028', 'CONSISTE NO PROCEDIMENTO PARA DIAGNOSTICAR OU TRATAR DOENÇAS CARDÍACAS, POR MEIO DA INTRODUÇÃO DE UM CATÉTER, QUE É UM TUBO FLEXÍVEL EXTREMAMENTE FINO E LONGO, NA ARTÉRIA DO BRAÇO OU DA PERNA DO INDIVÍDUO, QUE SERÁ CONDUZIDO ATÉ O CORAÇÃO. TAMBÉM DENOMINADO DE ANGIOGRAFIA CORONÁRIA, CINECORONARIOGRAFIA OU AINDA ESTUDO HEMODINÂMICO. É INDICADO NO DIAGNÓSTICO E TRATAMENTO DO INFARTO OU DA ANGINA, SENDO CAPAZ DE DETECTAR E REMOVER ACÚMULOS DE PLACAS DE GORDURA, COLESTEROL, CÁLCIO E OUTRAS SUBSTÂNCIAS ENCONTRADAS NO SANGUE, MOSTRAR SE AS PLACAS ESTREITARAM OU BLOQUEARAM AS ARTÉRIAS CORONÁRIAS. O CATETERISMO CARDÍACO EM PEDIATRIA É MUITO UTILIZADO PARA DIAGNOSTICAR E/OU TRATAR DIVERSAS CONDIÇÕES CARDÍACAS, VERIFICAR SE EXISTEM LESÕES NAS VÁLVULAS E DO MÚSCULO CARDÍACO, VERIFICAR A EXISTÊNCIA DE ALTERAÇÕES NA ANATOMIA DO CORAÇÃO NÃO CONFIRMADAS POR OUTROS EXAMES, MOSTRAR EM DETALHES MALFORMAÇÃO CONGÊNITA EM RECÉM-NASCIDOS E CRIANÇAS. FEITO SOB ANESTESIA LOCAL E SEDAÇÃO, É REALIZADA UMA PEQUENA ABERTURA PARA ENTRADA DO CATÉTER NA PELE DA VIRILHA OU DO ANTEBRAÇO SEGUIR É FEITA A INSERÇÃO DO CATÉTER NA ARTÉRIA QUE SERÁ CONDUZIDO ATÉ O CORAÇÃO. SÃO LOCALIZADAS AS ENTRADAS DAS ARTÉRIAS CORONÁRIAS DIREITA E ESQUERDA E A SEGUIR A INJEÇÃO DE CONTRASTE À BASE DE IODO QUE PERMITE A VISUALIZAÇÃO DAS IMAGENS DAS ARTÉRIAS E DO VENTRÍCULO ESQUERDO PARA VISUALIZAÇÃO DO BOMBEAMENTO CARDÍACO. BLOQUEIOS NAS ARTÉRIAS TAMBÉM PODEM SER VISTOS USANDO ULTRASSONOGRAFIA DURANTE O CATETERISMO CARDÍACO PARA AUXILIAR NA DEFINIÇÃO OU NÃO DA NECESSIDADE DE TRATAMENTO CIRÚRGICO (ANGIOPLASTIA CORONÁRIA, CIRURGIA CARDÍACA OU CORREÇÃO DE CARDIOPATIAS CONGÊNITAS). É UM EXAME INVASIVO QUE PODE SER REALIZADO DE FORMA ELETIVA EM CRIANÇAS, PARA CONFIRMAR A PRESENÇA DE OBSTRUÇÕES DAS ARTÉRIAS CORONÁRIAS OU AVALIAR O FUNCIONAMENTO DAS VALVAS E DO MÚSCULO CARDÍACO OU EM SITUAÇÕES DE EMERGÊNCIA, PARA DETERMINAR A EXATA LOCALIZAÇÃO DA MALFORMAÇÃO E PLANEJAR A MELHOR ESTRATÉGIA DE INTERVENÇÃO. TODO O EXAME ENVOLVE A AQUISIÇÃO E GERAÇÃO DE IMAGENS QUE SÃO POSTERIORMENTE DISPONIBILIZADAS, EM FILME E FOTOS, PARA O PACIENTE. QUANDO DA PUNÇÃO DA ARTÉRIA É INSTALADO UM INTRODUTOR, POR ONDE O CATETER SERÁ INTRODUZIDO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211020036', 'CONSISTE NA REALIZAÇÃO E LAUDO MÉDICO DE EXAME FEITO PARA AVALIAR A ATIVIDADE ELÉTRICA DO CORAÇÃO, OBSERVANDO O RITMO, A QUANTIDADE E A VELOCIDADE. É REALIZADO POR MEIO DE UM APARELHO COM ELETRODOS, QUE SÃO OS DISPOSITIVOS QUE LIGAM O PACIENTE COM O ELETROCARDIÓGRAFO. POR MEIO DELES É OBTIDA A INFORMAÇÃO ELÉTRICA PARA IMPRESSÃO E ANÁLISE DO ELETROCARDIOGRAMA. AS DERIVAÇÕES SÃO OS REGISTROS DA ATIVIDADE ELÉTRICA NO ELETROCARDIOGRAMA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211020044', 'CONSISTE NO EXAME QUE REGISTRA A ATIVIDADE ELÉTRICA DO CORAÇÃO E SUAS VARIAÇÕES DURANTE AS 24 HORAS DO DIA POR MEIO DE UM MONITOR PORTÁTIL. SÃO USADOS DE TRÊS A OITO ELETRODOS, CONFORME O MODELO DO APARELHO, ADERIDOS AO CORPO EM POSIÇÕES DETERMINADAS PELO FABRICANTE DO APARELHO E SEGUINDO PROTOCOLOS QUE POSSAM SER REPRODUZIDOS EM OUTROS SERVIÇOS PARA COMPARAÇÃO DOS RESULTADOS EM EXAMES FUTUROS. ESTES ELETRODOS SÃO CONECTADOS POR FIOS A UM RECEPTOR O QUAL REGISTRA A ATIVIDADE ELÉTRICA CARDÍACA DURANTE TODO O PERÍODO DE UM DIA INTEIRO E UMA NOITE, EM QUE ESTÁ CONECTADO. OS PACIENTES SÃO SOLICITADOS A REGISTRAR SUAS ATIVIDADES EM UM DIÁRIO FORNECIDO PELO SERVIÇO DE SAÚDE QUE INSTALA O APARELHO E BUSCA COMPARAR AS ATIVIDADES DO PACIENTE COM SEUS SINTOMAS. QUANDO O APARELHO É RETIRADO DO PACIENTE, OS DADOS CAPTADOS SÃO TRANSFERIDOS PARA UM COMPUTADOR, PARA SEREM ANALISADOS POSTERIORMENTE PELO ESPECIALISTA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211020052', 'CONSISTE NO EXAME QUE MEDE A PRESSÃO ARTERIAL A CADA 20 MINUTOS, DURANTE 24 HORAS, PARA A OBTENÇÃO DO REGISTRO DA PRESSÃO ARTERIAL DURANTE A VIGÍLIA E O SONO, COMO TAMBÉM DURANTE EVENTUAIS SINTOMAS COMO TONTURA, DOR NO PEITO E DESMAIO. ALÉM DISSO, POSSIBILITA A AVALIAÇÃO DA EFICÁCIA DO TRATAMENTO ANTI-HIPERTENSIVO. É FEITA A INSTALAÇÃO DO EQUIPAMENTO NO PACIENTE, QUE PERMANECE COM ELE DURANTE 24 HORAS. O EQUIPAMENTO É COMPOSTO POR UM MONITOR LEVE E PEQUENO, COLOCADO NA CINTURA, CONECTADO POR UM TUBO PLÁSTICO FINO A UMA BRAÇADEIRA COLOCADA NO BRAÇO NÃO DOMINANTE, EXCETO SE HOUVER ALGUMA CONTRAINDICAÇÃO. A CADA 20 MINUTOS O MONITOR INSUFLA A BRAÇADEIRA E REGISTRA A PRESSÃO OBTIDA. APÓS AS 24 HORAS, O PACIENTE RETORNA AO LOCAL DO EXAME PARA RETIRADA DO EQUIPAMENTO. O MONITOR É CONECTADO AO COMPUTADOR E UM SOFTWARE ESPECIALMENTE CONSTRUÍDO PARA ESTA FUNÇÃO DESENHA UM GRÁFICO DAS PRESSÕES REGISTRADAS NAS 24H. O EXAME DEVE SER REALIZADO EM UM DIA REPRESENTATIVO DA SUA ATIVIDADE DIÁRIA. ASSIM, É FUNDAMENTAL MANTER AS ATIVIDADES ROTINEIRAS. SERÁ FORNECIDO AO PACIENTE UM IMPRESSO CHAMADO DIÁRIO DE ATIVIDADES ONDE DEVEM SER ANOTADOS OS HORÁRIOS EM QUE DORMIU, ACORDOU, FEZ AS REFEIÇÕES, ASSIM COMO EVENTUAIS SINTOMAS E ATIVIDADES OU EVENTOS IMPORTANTES. É INDICADO NA SUSPEITA DE HIPERTENSÃO DO AVENTAL BRANCO, AVALIAÇÃO DA EFICÁCIA DO TRATAMENTO DA HIPERTENSÃO NAS 24 HORAS, TANTO NO SONO QUANTO NA VIGÍLIA, AVALIAÇÃO DE SINTOMAS, PRINCIPALMENTE OS RELACIONADOS À HIPOTENSÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211020060', 'CONSISTE NO EXAME COMPLEMENTAR PARA DIAGNÓSTICO DE DOENÇAS CARDIOVASCULARES, ALÉM DE SER ESSENCIAL PARA PESSOAS APARENTEMENTE SAUDÁVEIS COMO PREVENÇÃO, OU PARA AQUELAS COM CANSAÇO EXCESSIVO OU DORES NO PEITO. TAMBÉM É INDICADO PARA A INVESTIGAÇÃO DA CIRCULAÇÃO SANGUÍNEA CORONARIANA, PRINCIPALMENTE EM PESSOAS QUE TENHAM HISTÓRICO FAMILIAR DE DOENÇAS CARDIOVASCULARES. SÃO COLOCADOS 13 ELETRODOS NO PEITO DO PACIENTE PARA REGISTRO POR MEIO DE ELETROCARDIOGRAMA (ECG) ANTES DA PROVA FÍSICA NA ESTEIRA OU BICICLETA ERGOMÉTRICA E INICIAR O EXAME PARA QUE SEJA OBSERVADO O COMPORTAMENTO DA FREQUÊNCIA CARDÍACA E DA PRESSÃO ARTERIAL DURANTE O ESTADO DE REPOUSO E ESFORÇO. O TESTE OFERECE DADOS SOBRE O FUNCIONAMENTO CARDIOVASCULAR QUANDO O CORAÇÃO É SUBMETIDO A ESFORÇO FÍSICO DE FORMA GRADUAL E CRESCENTE E AVALIA O DESEMPENHO E A CAPACIDADE DOS VASOS DO CORAÇÃO AUMENTAREM O FLUXO SANGUÍNEO CONFORME A INTENSIDADE DO ESFORÇO, AVALIAR A CAPACIDADE CARDIORRESPIRATÓRIA E VERIFICAR A EXISTÊNCIA DE ARRITMIAS, ISQUEMIA MIOCÁRDICA E DOENÇA ARTERIAL CORONARIANA. DEVE SER LEVADO EM CONTA IDADE E LIMITAÇÕES FÍSICAS DO PACIENTE. O PACIENTE É MONITORADO E O MOVIMENTO COMEÇA LENTAMENTE E AOS POUCOS A VELOCIDADE AUMENTA. DEPOIS QUE O ESFORÇO MÁXIMO FOR ALCANÇADO, O MOVIMENTO É PROGRESSIVAMENTE DESACELERADO PARA A FASE DE DESAQUECIMENTO. O EXAME DEVE SER INTERROMPIDO CASO O PACIENTE APRESENTE GRANDE CANSAÇO OU EXAUSTÃO OU A PRESSÃO ELEVAR OU BAIXAR ABRUPTAMENTE, ASSIM COMO ALTERAÇÕES NO RITMO CARDÍACO E ANORMALIDADES CARDIOVASCULARES. O TESTE É CONTRAINDICADO PARA PACIENTES COM PERICARDITES E MIOCARDITES AGUDAS, EMBOLIA PULMONAR, ARRITMIAS NÃO CONTROLADAS, ESTENOSE AÓRTICA GRAVE, LIMITAÇÕES FÍSICAS E GESTANTES.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211020079', 'CONSISTE NA AFERIÇÃO DA SATURAÇÃO DE OXIGÊNIO, EM RECÉM NASCIDO APARENTEMENTE SAUDÁVEL A PARTIR DE 34 SEMANAS DE GESTAÇÃO, NAS PRIMEIRAS 24 A 48 HORAS DE VIDA , POR MEIO DO OXÍMETRO DE PULSO, ANTES DA ALTA DA UNIDADE NEONATAL. O OXÍMETRO É COLOCADO EM MEMBRO SUPERIOR DIREITO E EM UM DOS DOS MEMBROS INFERIORES PARA A ADEQUADA AFERIÇÃO. É NECESSÁRIO QUE O RECÉM-NASCIDO ESTEJA COM AS EXTREMIDADES AQUECIDAS E O MONITOR EVIDENCIE UMA ONDA DE TRAÇADO HOMOGÊNEA. O RESULTADO SERÁ CONSIDERADO ANORMAL CASO QUALQUER MEDIDA DA SPO2 SEJA MENOR QUE 95% OU HOUVER UMA DIFERENÇA IGUAL OU MAIOR QUE 3% ENTRE AS MEDIDAS DO MEMBRO SUPERIOR DIREITO E MEMBRO INFERIOR. CASO HAJA ALTERAÇÃO UMA NOVA AFERIÇÃO DEVERÁ SER REALIZADA APÓS 1 HORA.', '202507', 1);
