INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211040010', 'CONSISTE NO EXAME DIRETO DO LÍQUIDO AMNIÓTICO E DAS MEMBRANAS, REALIZADO NO FINAL DE UMA GRAVIDEZ. É A VISUALIZAÇÃO DIRETA DO ASPECTO DO LÍQUIDO AMNIÓTICO ATRAVÉS DE UM TUBO FINO CHAMADO AMNIOSCÓPIO, COM O OBJETIVO DE AVALIAR A MATURIDADE E O RISCO DE SOFRIMENTO FETAL. A AMNIOSCOPIA É INDICADA PARA PACIENTES EM TRABALHO DE PARTO OU ÀQUELAS QUE JÁ PASSARAM DAS 40 SEMANAS DE GESTAÇÃO. PARA SER REALIZADO, O COLO UTERINO PRECISA ESTAR DILATADO EM AO MENOS UM CENTÍMETRO, A BOLSA AMNIÓTICA TEM QUE ESTAR ÍNTEGRA E A PLACENTA NÃO PODE SER DE INSERÇÃO BAIXA OU PRÓXIMA DO COLO DO ÚTERO. DURANTE O TOQUE VAGINAL, O AMNIOSCÓPIO É INSERIDO NA VAGINA E NO COLO UTERINO, PARA QUE SE VERIFIQUE SE O LÍQUIDO AMNIÓTICO ESTÁ CLARO OU SE HÁ MECÔNIO, O QUE CARACTERIZA ALGUM ESTRESSE/SOFRIMENTO FETAL E DÁ INDICAÇÃO DE ALERTA AO OBSTETRA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211040029', 'CONSISTE NO PROCEDIMENTO DESTINADO À ANÁLISE DO COLO DO ÚTERO E DOS TECIDOS DA VAGINA E DA VULVA, ALÉM DE AVALIAR A REGIÃO ANAL E PERIANAL. O EXAME É REALIZADO UTILIZANDO-SE O COLPOSCÓPIO QUE POR MEIO DE LENTES DE AUMENTO VISA DIAGNÓSTICAR LESÕES BENINGNAS, PRÉ-MALIGNAS OU MALIGNAS, ASSIM COMO DOENÇAS SEXUALMENTE TRANSMISSIVÉIS, ESPECIALMENTE O VÍRUS HPV. FAZ PARTE DO EXAME APLICAR ÁCIDO ACÉTICO NO COLO DO UTERO, O QUE POSSIBILITA O APARECIMENTO DE LESÕES QUE INICIALMENTE NÃO ERAM EVIDENTES, ALÉM DE LUGOL PARA COMPLETAR A CARACTERIZAÇÃO DE LESÕES VISUALIZADAS (TESTE DE SCHILLER) NO MESMO MOMENTO DE PODER SER COLHIDA SECREÇÃO PARA O EXAME PAPANICOLAU, OU AINDA REALIZAR BIÓPSIA DOS TECIDOS, SE HOUVR LESOES. A COLETA DE SECREÇÃO E A BIOSPIA NÃO ESTÃO INCLUIDAS NESTE PROCEDIMENTO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211040037', 'CONSISTE NO EXAME LABORATORIAL QUE ANALISA, UTILIZANDO UM MICROSCOPIO, UMA AMOSTRA DE SECREÇÃO VAGINAL DILUÍDO EM MEIO SALINO PARA DIAGNOSTICAR VAGINITES E VAGINOSE. É UM MÉTODO SIMPLES E PODE SER REALIZADO POR PROFISSIONAL DE SAÚDE CAPACITADO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211040045', 'A HISTEROSCOPIA DIAGNÓSTICA É O EXAME REALIZADO PARA OBSERVAR A CAVIDADE UTERINA E O CANAL CERVICAL. PODE SER REALIZADA EM AMBULATÓRIO SEM O USO DA ANESTESIA E SEM EXIGIR INTERNAÇÃO. PERMITE A VISUALIZAÇÃO DIRETA DO INTERIOR DO ÚTERO, COM INTRODUÇÃO DE INSTRUMENTAL E UMA ÓTICA VIA VAGINAL QUE VARIA DE 1,2MM A 4 MM DE DIÂMETRO. ATRAVÉS DA VÍDEOHISTEROSCOPIA, INTRODUZ-SE PELA VAGINA UMA FINA ÓPTICA NO CANAL UTERINO, QUE LEVA LUZ AO SEU INTERIOR, BEM COMO UM GÁS (GÁS CARBÔNICO) PARA DISTENDÊ-LA, TUDO CONTROLADO PELO HISTEROFLATOR AUTOMÁTICO QUE OFERECE PROTEÇÃO E SEGURANÇA QUANTO À ABSORÇÃO DE CO² PELA PACIENTE. ACOPLA-SE MICRO CÂMERA QUE LEVA A IMAGEM A UM MONITOR QUE PERMITE A VISUALIZAÇÃO DO CANAL CERVICAL E AS POSSÍVEIS PATOLOGIAS. APÓS O EXAME A PACIENTE PODERÁ RETORNAR ÀS SUAS ATIVIDADES NORMAIS. O EXAME É FOTOGRAFADO. INDICAÇÕES DIAGNÓSTICAS: INFERTILIDADE, ABORTAMENTO HABITUAL, SANGRAMENTO UTERINO ANORMAL, PÓLIPOS, MIOMAS, ADERÊNCIAS, ESPESSAMENTO DO ENDOMÉTRIO E ADENOCARCINOMA DO ENDOMÉTRIO.||||', '202507', 1);
