INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211050105', 'É O MÉTODO DIAGNÓSTICO MAIS OBJETIVO PARA A AVALIAÇÃO DO SONO E DE SUAS VARIÁVEIS FISIOLÓGICAS. ATRAVÉS DO REGISTRO DE TRÊS PARÂMETROS MÍNIMOS: ELETRENCEFALOGRAMA, ELETRO-OCULOGRAMA E ELETROMIOGRAMA SUB-MENTONIANO QUANTIFICA E QUALIFICA O SONO DO INDIVIDUO. REGISTRA RONCO, FLUXO DE AR, OXIGENAÇÃO, POSIÇÃO E PARÂMETROS ACESSÓRIOS COMO O FLUXO AÉREO NASAL, A OXIMETRIA, O ESFORÇO RESPIRATÓRIO, O ELTOCARDIOGRAMA, O ELETROMIOGRAMA TIBIAL ANTERIOR, DENTRE OUTROS, CONTRIBUINDO PARAO DIAGNÓSTICO DE DOENÇAS RELACIONADAS AO SONO.||A POLISSONOGRAFIA PODE SER FEITA NO LABORATÓRIO DO SONO EM AMBIENTE HOSPITALAR OU EM DOMICILIO. NO DOMICILIO SÃO UTILIZADOS MONITORES ESPECIAS MINIATURIZADOS CAPAZES DE DETECTAR MÚLTIPLAS VARIÁVEIS RESPIRATÓRIAS DURANTE UMA NOITE E ARMAZENÁ-LA EM SUA MEMORIA. ALÉM DE OXIMETRIA E FREQUÊNCIA DE PULSO, MEDEM FLUXO AÉREO POR TERMISTOR, SOM RESPIRATÓRIO E RONCO CAPTADOS POR MICROFONE, POSIÇÃO DO CORPO, MOVIMENTOS DO CORPO E RESPIRATÓRIOS E, PODEM INCLUIR EEG, EOG E ECG.', '202507', 1);
