INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211080055', 'CONSISTE NA PROVA DA FUNÇÃO PULMONAR QUE PERMITE O DIAGNÓSTICO E A QUANTIFICAÇÃO DOS DISTÚRBIOS VENTILATÓRIOS, AVALIANDO SE A QUANTIDADE DE AR INSPIRADO É SUFICIENTE PARA O INDIVÍDUO OU SE HÁ ALGUMA OBSTRUÇÃO À PASSAGEM DO AR, COMO A PRESENÇA DE UM CORPO ESTRANHO, DIMINUIÇÃO DO TAMANHO DOS BRÔNQUIOS POR REAÇÃO ALÉRGICA COMO OCORRE NO CASO DA ASMA, OU POR SECREÇÕES NO LOCAL. O INDIVÍDUO SOPRA O AR PARA DENTRO DO ESPIRÔMETRO COM A MAIOR FORÇA POSSÍVEL. APÓS USA UM MEDICAMENTO BRONCODILATADOR E REALIZA NOVAMENTE O SOPRO NO APARELHO, E UM COMPUTADOR REGISTRA TODOS OS DADOS OBTIDOS PARA ANÁLISE SE HÁ AUMENTO DA QUANTIDADE DE AR INSPIRADO APÓS O USO DO MEDICAMENTO.', '202507', 1);
