INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211080080', 'CONSISTE EM LEVAR O PACIENTE A REALIZAR UMA CAMINHADA DE 6 MINUTOS PARA AVALIAR SUA CAPACIDADE RESPIRATÓRIA, CARDÍACA E METABÓLICA.  O TESTE VERIFICA A DISTÂNCIA QUE A PESSOA CONSEGUE CAMINHAR NUM LOCAL PLANO, COM PELO MENOS 30 METROS DE COMPRIMENTO, DURANTE 6 MINUTOS CRONOMETRADOS, VERIFICANDO-SE A FREQUÊNCIA CARDÍACA E A PRESSÃO ARTERIAL, ANTES, DURANTE E APÓS A CAMINHADA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211090018', 'CONSISTE NO REGISTRO DE ALTERAÇÕES RELACIONADAS AO ARMAZENAMENTO E ELIMINAÇÃO DA URINA, É UM EXAME QUE TEM COMO OBJETIVO DEMONSTRAR A FUNÇÃO DO TRATO URINÁRIO INFERIOR, MAIS ESPECIFICAMENTE EVIDENCIA SE A BEXIGA CONSEGUE CUMPRIR SUA FUNÇÃO: ARMAZENAR URINA SOB BAIXA PRESSÃO E PROPORCIONAR ADEQUADO ESVAZIAMENTO (MICÇÃO NORMAL). CONSISTE EM: 1. URO-FLUXOMETRIA (É A MEDIDA DO FLUXO URINÁRIO (VOLUME DE URINA QUE PASSA PELA URETRA EM UMA UNIDADE DE TEMPO) EM ML/S. 2. CISTOMETRIA QUE RELACIONA A PRESSÃO-VOLUME DURANTE O ENCHIMENTO VESICAL. 3. ESTUDOS MICCIONAIS DE FLUXO E PRESSÃO. (DURANTE A MICÇÃO, PRESSÃO INTRAVESICAL E FLUXO URINÁRIO SÃO MENSURADOS CONTINUAMENTE). 4. ESTUDOS DE PRESSÃO URETRAL (MOSTRA O PERFIL DE PRESSÃO URETRAL E AVALIA A PRESSÃO AO LONGO DA URETRA, ASSIM COMO AVALIA A PRESSÃO DE FECHAMENTO URETRAL AO LONGO DO TRAJETO COMPREENDIDO ENTRE O COLO VESICAL E O MEATO URETRAL EXTERNO).', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211090026', 'CONSISTE NO PROCEDIMENTO TAMBÉM CHAMADO DE CATETERISMO VESICAL E QUE CONSISTE NA INTRODUÇÃO DE UMA SONDA NA URETRA ATÉ À BEXIGA, SENDO MÉTODO PADRÃO PARA ACESSAR A BEXIGA URINÁRIA, INDICADO ANTES DE CIRURGIAS QUE EXIJAM ESVAZIAR A BEXIGA, QUANDO HÁ RETENÇÃO DE URINA,QUANDO HÁ OBSTRUÇÃO DO TRATO URINÁRIO, PARA LAVAGEM DA BEXIGA, PARA MONITORAR O DÉBITO URINÁRIO, QUANDO O PACIENTE É INCONTINENTE.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211090034', 'CONSISTE NO REGISTRO DO COMPORTAMENTO PRESSÓRICO VESICAL DURANTE O CICLO MICCIONAL. IDENTIFICA A AÇÃO DO MÚSCULO DETRUSOR (PDET) E DA PRENSA ABDOMINAL (PABD) NO CICLO MICCIONAL. PERMITE A AVALIAÇÃO DA COMPLACÊNCIA, SENSIBILIDADE E CAPACIDADE DA BEXIGA. ALÉM DE AVALIAR PARÂMETROS COMO FASE DE ENCHIMENTO VESICAL, TESTE DE ESFORÇO, PRESSÃO DE PERDA SOB ESFORÇO E A FASE DE ESVAZIAMENTO VESICAL. O EXAME É REALIZADO POR MEIO DE UMA SONDA VIA URETRAL (OU EM CASOS RAROS SUPRA PÚBICA) E DE UMA SONDA RETAL CONECTADOS A TRANSDUTORES DE PRESSÃO. AS MANOBRAS DE ESFORÇO SÃO SOLICITADAS PELO UROLOGISTA PARA QUE POSSA VERIFICAR A PRESENÇA DE PERDA DE URINA SOB O ESFORÇO. A CISTOMETRIA PODE SER REALIZADA COM INFUSÃO DE LÍQUIDO (ÁGUA, SOLUÇÃO SALINA OU CONTRASTE RADIOPACO) OU GÁS (AR OU DIÓXIDO DE CARBONO) ATRAVÉS DA GRAVIDADE OU DE UMA BOMBA PERISTÁLTICA.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211090042', 'CONSISTE NO EXAME QUE PERMITE AVALIAR A CAPACIDADE VESICAL, COMPLACÊNCIA, A SENSIBILIDADE DA BEXIGA E A ATIVIDADE DETRUSORA E REGISTRA A PRESSÃO INTRAVESICAL DURANTE ADMINISTRAÇÃO DE SORO FISIOLÓGICO, EM TEMPERATURA AMBIENTE, POR MEIO DE SONDA URETRAL.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211090050', 'CONSISTE NA VERIFICAÇÃO DA PRESSÃO INTRA-ABDOMINAL. SÃO DESCRITOS VÁRIOS MÉTODOS PARA MEDIR A PRESSÃO INTRA-ABDOMINAL, SENDO: ELES, A MEDIÇÃO DIRETA INTRAPERITONIAL POR MEIO DE UM CATETER DE DIÁLISE PERITONEAL; O MÉTODO INTRAGÁSTRICO VIA TUBO NASOGÁSTRICO; VIA RETAL E POR MEIO DE UM CATETER URINÁRIO NA BEXIGA, SENDO ESTE ÚLTIMO O MAIS UTILIZADO.||É INDICADA EM CASOS DE RISCO PARA DESENVOLVIMENTO DE HIPERTENSÃO INTRA-ABDOMINAL OU SÍNDROME COMPARTIMENTAL AGUDA. ||', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211090069', 'CORRESPONDE À CURVA TOPOGRÁFICA MANOMÉTRICA REPRESENTATIVA DA PRESSÃO URETRAL E SUA DISTRIBUIÇÃO AO LONGO DA URETRA. REPRESENTA A FUNÇÃO URETRAL PASSIVA, SENDO UM DOS TESTES URODINÂMICOS QUE AVALIA A FUNÇÃO DA URETRA, TAMBÉM CHAMADO DE TÉCNICA DE BROWN E WICKHAM. OS PARÂMETROS AVALIADOS NO PPU SÃO: A FORMA DA CURVA, A PRESSÃO URETRAL MÁXIMA (PUM), PRESSÃO MÁXIMA DE FECHAMENTO URETRAL (PMFU), O COMPRIMENTO FUNCIONAL URETRAL (CFU) E A ÁREA DE CONTINÊNCIA, QUE É A ÁREA DA CURVA QUE SE MANTÉM ACIMA DA PRESSÃO VESICAL.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0211090077', 'CONSISTE NO EXAME QUE PERMITE ESTUDAR A VELOCIDADE DO FLUXO URINÁRIO, OU SEJA, A RAPIDEZ COM QUE A URINA É EXPELIDA E O VOLUME DE URINA ELIMINADA DURANTE A MICÇÃO E PERMITE UMA ANÁLISE MAIS OBJETIVA, COM REGISTO DE VALORES E ESQUEMATIZAÇÃO EM FORMA DE GRÁFICO DAS VARIÁVEIS MEDIDAS. PERMITE RECOLHER OS VALORES DOS SEGUINTES PARÂMETROS: O TEMPO NECESSÁRIO PARA COMEÇAR A URINAR, A FORÇA DO JATO URINÁRIO E A CONTINUIDADE DO FLUXO URINÁRIO, A QUANTIDADE DE URINA EXPELIDA (EM ML) E O TEMPO QUE DEMORA A ESVAZIAR A BEXIGA. PERMITE OBSERVAR A PRESENÇA DE OBSTRUÇÃO DO TRATO URINÁRIO ABAIXO DA BEXIGA OU DIMINUIÇÃO DE CONTRATILIDADE DA BEXIGA, A PARTICIPAÇÃO DE MECANISMOS AUXILIARES DA MICÇÃO COMO OS MÚSCULOS ABDOMINAIS OU A INCAPACIDADE EM ESVAZIAMENTO VESICAL COMPLETO.', '202507', 1);
