INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0301010382', 'CONSISTE NA AVALIAÇÃO REALIZADA PARA IDENTIFICAÇÃO DO RISCO DE EVENTOS E DOENÇAS CARDIOVASCULARES (DCV) DE UM INDIVÍDUO A PARTIR DE FATORES CLÍNICOS (POR EXEMPLO, PRESSÃO ARTERIAL, TABAGISMO, ÍNDICE DE MASSA CORPÓREA,  DIABETES, ENTRE OUTROS), INCLUINDO, PREFERENCIALMENTE, A ANÁLISE DE EXAMES LABORATORIAIS. A ESTRATIFICAÇÃO DO RISCO CARDIOVASCULAR TEM POR OBJETIVO ORIENTAR A PREVENÇÃO E O TRATAMENTO EFETIVO COM A CORRETA DEFINIÇÃO DE METAS TERAPÊUTICAS, A PERIODICIDADE DE ACOMPANHAMENTO E A INSTITUIÇÃO DE MEDIDAS FARMACOLÓGICAS E NÃO FARMACOLÓGICAS. PARA ESTIMAR O RISCO DE DCV, RECOMENDA-SE A UTILIZAÇÃO DE ALGORITMOS E ESCORES ESPECÍFICOS DESENVOLVIDOS A PARTIR DE ESTUDOS POPULACIONAIS. RESSALTA-SE QUE ALGUMAS CONDIÇÕES CLÍNICAS PODEM SER CONSIDERADAS DEFINIDORAS DE ALTO RISCO, CONFORME PROTOCOLOS DO MINISTÉRIO DA SAÚDE, DISPENSANDO A NECESSIDADE DE CÁLCULO PARA ESTRATIFICAÇÃO DO RISCO CARDIOVASCULAR.', '202507', 1);
