INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0301020035', 'É O PROCESSO DE INVESTIGAÇÃO DA RELAÇÃO ENTRE O AGRAVO OU A DOENÇA COM O TRABALHO, A QUAL DEVE SER DESENVOLVIDA POR UMA EQUIPE MULTIDISCIPLINAR, SENDO O (A) MÉDICO (A) RESPONSÁVEL PELO ESTABELECIMENTO DO DIAGNÓSTICO FRUTO DA INVESTIGAÇÃO, ESTA AÇÃO EXIGE O RECONHECIMENTO DO (A) USUÁRIO (A)-TRABALHADOR (A), A PARTIR DO ACOLHIMENTO E DA ESCUTA QUALIFICADA, COM O OBJETIVO DE IDENTIFICAR OS CASOS SUSPEITOS DE DOENÇAS OU AGRAVOS RELACIONADOS AO TRABALHO, COM ÓBITO OU NÃO, A ABORDAGEM DEVE CONSIDERAR A POSSÍVEL RELAÇÃO DAS DEMANDAS DE SAÚDE, QUEIXAS E SINTOMAS REFERIDOS, O AGRAVO OU DOENÇA E SUA POSSÍVEL RELAÇÃO COM O TRABALHO ATUAL E/OU PROGRESSO, EM CASO DE CONFIRMAÇÃO DO AGRAVO OU DOENÇA COM O TRABALHO, REALIZAR O REGISTRO NO SISTEMA DE INFORMAÇÃO DE AGRAVOS DE NOTIFICAÇÃO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0301020043', 'A INVESTIGAÇÃO EPIDEMIOLÓGICA DE ÓBITO E SUA RELAÇÃO COM O TRABALHO É UMA IMPORTANTE ESTRATÉGIA PARA DEFINIÇÃO DE AÇÕES PARA IDENTIFICAÇÃO, CONTROLE E PREVENÇÃO DE RISCOS NOS AMBIENTES E PROCESSOS DE TRABALHO. PARA REALIZAR A INVESTIGAÇÃO EPIDEMIOLÓGICA DE ÓBITOS RELACIONADOS AO TRABALHO VOCÊ DEVE IDENTIFICAR OS ÓBITOS QUE SERÃO INVESTIGADOS; ANALISAR OS CAMPOS DA DECLARAÇÃO DE ÓBITO; E REALIZAR A INVESTIGAÇÃO DE CAMPO. DEVEM SER REGISTRADAS AS INVESTIGAÇÕES EPIDEMIOLÓGICAS DO ÓBITO E DA RELAÇÃO COM O TRABALHO, INDEPENDENTEMENTE DA CONCLUSÃO DA INVESTIGAÇÃO: O ÓBITO TEM RELAÇÃO COM O TRABALHO; O ÓBITO NÃO TEM RELAÇÃO COM O TRABALHO OU NÃO FOI POSSÍVEL DEFINIR A RELAÇÃO DO ÓBITO COM O TRABALHO.', '202507', 1);
