INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0301080402', 'O APOIO MATRICIAL É UM MODO DE PRODUZIR SAÚDE EM QUE PELO MENOS UMA EQUIPE DE VIGILÂNCIA EM SAÚDE DO TRABALHADOR E UMA EQUIPE DE ATENÇÃO PRIMÁRIA, NUM PROCESSO DE CONSTRUÇÃO COMPARTILHADA CRIAM UMA PROPOSTA DE INTERVENÇÃO PEDAGÓGICO-TERAPÊUTICA COM O INTUITO DE GARANTIR A INTEGRALIDADE DA ATENÇÃO À SAÚDE DO TRABALHADOR. DEVE SER REALIZADO DE FORMA CONJUNTA ENTRE AS EQUIPES MULTIDISCIPLINARES. O APOIO DEVE SER VOLTADO TANTO PARA O CUIDADO ASSISTENCIAL E DE VIGILÂNCIA AOS USUÁRIOS DO SUS QUANTO DE CARÁTER TÉCNICO PEDAGÓGICO AOS TRABALHADORES DA SAÚDE DA ATENÇÃO PRIMÁRIA. PODERÃO SER REALIZADAS POR MEIO DE FERRAMENTAS COMO: DISCUSSÕES DE CASOS, CONSTRUÇÃO DE PROJETOS TERAPÊUTICOS SINGULARES; CONSULTAS COMPARTILHADAS; ATIVIDADES EM GRUPO; ORIENTAÇÕES SOBRE VIGILÂNCIA DOS AMBIENTES E PROCESSOS DE TRABALHO; VIGILÂNCIA EPIDEMIOLÓGICA DE DOENÇAS E AGRAVOS RELACIONADOS AO TRABALHO; TERRITORIALIZAÇÃO E ANÁLISE DE SITUAÇÃO DE SAÚDE DO TRABALHADOR; REUNIÕES DAS EQUIPES INTEGRADAS E OUTROS.', '202507', 1);
