INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0303020032', 'TRATAMENTO CLÍNICO QUE VISA SOLUCIONAR O PRINCIPAL PROBLEMA DA ASSISTÊNCIA HOSPITALAR DO PACIENTE COM O FOCO NA PATOLOGIA RESPONSÁVEL PELA INTERNAÇÃO INICIAL OU EM PATOLOGIA SUPERVENIENTE DE MAIOR GRAVIDADE OU COMPLEXIDADE QUE DETERMINAM OS CUIDADOS DE SAÚDE APROPRIADOS AO ATENDIMENTO AO PACIENTE EM CIRCUNSTÂNCIAS CLÍNICAS ESPECÍFICAS. NESTE CASO, A ANEMIA APLÁSTICA OU APLÁSICA É O RESULTADO DA DIMINUIÇÃO SIMULTÂNEA DO NÚMERO DE HEMÁCIAS E LEUCÓCITOS DO SANGUE OU PANCITOPENIA DECORRENTE DA DIMINUIÇÃO DA HEMATOPOIESE, OU SEJA, DO PROCESSO DE PRODUÇÃO DE CÉLULAS SANGUÍNEAS. ESSA DISFUNÇÃO PODE TER ORIGEM CONGÊNITA, IDENTIFICADA NO NASCIMENTO OU NOS PRIMEIROS MESES DE VIDA DO INDIVÍDUO, OU ORIGEM ADQUIRIDA, COMO USO DE ALGUNS TIPOS DE MEDICAMENTOS, DOENÇAS AUTOIMUNES, INFECÇÕES VIRAIS E EXPOSIÇÃO A SUBSTÂNCIAS QUÍMICAS TÓXICAS COMO O BENZENO E DEFENSIVOS AGRÍCOLAS CONVENCIONAIS. INDIVÍDUOS ACOMETIDOS APRESENTAM SINTOMAS COMO FADIGA, FALTA DE AR, TAQUICARDIA E PALIDEZ, INFECÇÕES E HEMORRAGIAS.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0303020040', 'TRATAMENTO CLÍNICO QUE VISA SOLUCIONAR O PRINCIPAL PROBLEMA DA ASSISTÊNCIA HOSPITALAR DO PACIENTE COM O FOCO NA PATOLOGIA RESPONSÁVEL PELA INTERNAÇÃO INICIAL OU EM PATOLOGIA SUPERVENIENTE DE MAIOR GRAVIDADE OU COMPLEXIDADE QUE DETERMINAM OS CUIDADOS DE SAÚDE APROPRIADOS AO ATENDIMENTO AO PACIENTE EM CIRCUNSTÂNCIAS CLÍNICAS ESPECÍFICAS. NESTE CASO, A ANEMIA HEMOLÍTICA É CAUSADA PELA RUPTURA DAS HEMÁCIAS (HEMÓLISE) ANTES DO PERÍODO DE VIDA NORMAL DE 120 DIAS. ESSA RUPTURA ANORMAL PODE OCORRER NOS VASOS SANGUÍNEOS (HEMÓLISE INTRAVASCULAR) OU EM OUTRO ÓRGÃO (EXTRAVASCULAR). TEM DIVERSAS CAUSAS E SUA INTENSIDADE PODE VARIAR DE INOFENSIVA ATÉ AMEAÇAR A VIDA, PODENDO SER CONGÊNITA OU ADQUIRIDA. O TRATAMENTO DEPENDE DA CAUSA E NATUREZA DA QUEBRA DAS HEMÁCIAS. OS SINTOMAS DE ANEMIA MODERADA SÃO: FRAQUEZA E CANSAÇO, PALIDEZ OU PELE AMARELADA, FALTA DE APETITE, TONTURA E SONOLÊNCIA, TAQUICARDIA COM HIPOTENSÃO, RESPIRAÇÃO ACELERADA, FALTA DE AR.', '202507', 1);
