INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0401020053', 'CONSISTE NA RETIRADA DE UMA LESÃO DE PELE NOS CASOS EM QUE NÃO É POSSÍVEL A RETIRADA POR MEIO DE UMA EXCISÃO E SUTURA SIMPLES, DEVIDO AO SEU TAMANHO OU LOCALIZAÇÃO. PARA POSSIBILITAR O FECHAMENTO DA FERIDA CIRÚRGICA PODE-SE UTILIZAR A TÉCNICA DA Z-PLASTIA, QUE CONSISTE EM FAZER INCISÕES EM FORMATO DE TRIANGULO, CUJOS LADOS SÃO UNIDOS, FORMANDO UMA CICATRIZ EM FORMATO DE Z OU PODE SER FEITA  A  ROTAÇÃO DE UMA ÁREA DA PELE CONTÍGUA À LESÃO PARA POSSIBILITAR O FECHAMENTO DA FERIDA CIRÚRGICA. EXIGEM ANESTESIA LOCAL E A FERIDA É FECHADA COM PONTOS INTERNOS E EXTERNOS, QUE SERÃO RETIRADOS DENTRO DE 7 A 15 DIAS. ESTE PROCEDIMENTO É SEMPRE A PRIMEIRA OPÇÃO DE RECONSTRUÇÃO QUANDO NÃO FOR POSSÍVEL O FECHAMENTO POR SUTURA SIMPLES, EVITANDO O USO DE ENXERTOS DE PELE DE OUTROS LOCAIS QUE PODEM DEIXAR DIFERENÇA DE COR E TEXTURA EM RELAÇÃO À PELE AO REDOR DA ÁREA ENXERTADA. OS RETALHOS PODEM SER POR DESLIZAMENTO, ROTAÇÃO OU TRANSPOSIÇÃO.QUANDO IDENTIFICADO QUE ESTE PROCEDIMENTO DECORRE DE ATO DE VIOLÊNCIA  CONTRA MULHER, RECOMENDA-SE O REGISTRO NO CAMPO DIAGNÓSTICO SECUNDÁRIO DA AIH DE UM OU MAIS CID RELACIONADOS PELA PORTARIA INTERMINISTERIAL Nº331 DE 08/03//2016: R45.6,T74.1,T74.2,T74.8,X86,X89,X90,X93,X94,X95,X96,X97,X98,X99,Y01,Y02, Y03, Y04, Y05, Y07,Y08, Y09, Y56,W50', '202507', 1);
