INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0412010038', 'PROCEDIMENTO QUE CONSISTE EM COLOCAÇÃO DE PRÓTESE LARINGO-TRAQUEAL, TRAQUEAL, TRAQUEO-BRÔNQUICA, BRÔNQUICA POR VIA ENDOSCÓPICA. (INCLUI A PRÓTESE). EXISTEM DIVERSOS MODELOS DE ÓRTESES, SENDO QUE CADA UMA DELAS TEM INDICAÇÃO PRECISA. A ÓRTESE É COLOCADA NA TRAQUÉIA COM UM RAMO PARA CIMA DA ESTENOSE (EM DIREÇÃO À BOCA) E UM ABAIXO (EM DIREÇÃO AOS PULMÕES). A ÓRTESE OU MOLDE TRAQUEAL FICA SOB A ESTENOSE E GERALMENTE JÁ É SUFICIENTE PARA MANTÊ-LA ABERTA, ASSIM O PACIENTE VOLTA A RESPIRAR PELO NARIZ E PODE FALAR NORMALMENTE. O PROCEDIMENTO DEVE SER REALIZADO POR BRONCOSCOPIA RÍGIDA, NO CENTRO CIRÚRGICO E SOB ANESTESIA GERAL. INICIALMENTE, PELA BRONCOSCOPIA FAZ-SE UMA INSPEÇÃO DA TRAQUÉIA, REAVALIANDO O GRAU E EXTENSÃO DA ESTENOSE. APÓS CONFIRMAÇÃO DA NECESSIDADE DA ÓRTESE, ESTE É IMPLANTADO (COLOCADO) E MOLDA TODA EXTENSÃO DA ESTENOSE. A TÉCNICA VARIA CONFORME O MODELO DE ÓRTESE ESCOLHIDO. PELA BRONCOSCOPIA CONFIRMA-SE A POSIÇÃO E O PROCEDIMENTO É ENCERRADO. ESTE PROCEDIMENTO É UTILIZADA EM SITUAÇÕES ONDE NÃO EXISTA OU NÃO SE EXIGE UMA TRAQUEOSTOMIA. A BRONCOSCOPIA NÃO ESTÁ INCLUÍDA NO PROCEDIMENTO, PODENDO SER REGISTRADA EM SEPARADO.', '202507', 1);
INSERT INTO public."tb_descricao" ("co_procedimento", "ds_procedimento", "dt_competencia", "version_id") VALUES ('0412010046', 'PROCEDIMENTO QUE CONSISTE EM COLOCAÇÃO DE PRÓTESE ENDOTRAQUEAL E TRAQUEO-BRONQUICA PARA TRATAMENTO DA ESTENOSE TRAQUEOBRÔNQUICA CAUSADA POR DIVERSAS CONDIÇÕES COMO TUMORES, COMPRESSÃO EXTRÍNSECA, TRAQUEOBRONCOMALACIA E COMPLICAÇÃO RESULTANTE DE INTUBAÇÃO PROLONGADA E PODEM SER MANTIDOS NA LUZ TRAQUEAL POR LONGOS PERÍODOS DE TEMPO. INCLUI A PRÓTESE.', '202507', 1);
